mapper.select:
select `id`,`member_number`,`sale_number`,`cost_type`,`shorthand_code`,`cost_name`,`cost_money`,`arrear_money`,`sale_name`,`is_sale`,`create_time`,`type`,`original_price` from cost

     and id = #{id} 
     and member_number like concat('%', #{memberNumber},'%') 
     and sale_number like concat('%', #{saleNumber},'%') 
     and cost_type = #{costType} 
     and shorthand_code = #{shorthandCode} 
     and cost_name = #{costName} 
     and cost_money = #{costMoney} 
     and arrear_money = #{arrearMoney} 
     and sale_name = #{saleName} 
     and is_sale = #{isSale} 



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

select count(*) from cost

     and id = #{id} 
     and member_number like concat('%', #{memberNumber},'%') 
     and sale_number like concat('%', #{saleNumber},'%') 
     and cost_type = #{costType} 
     and shorthand_code = #{shorthandCode} 
     and cost_name = #{costName} 
     and cost_money = #{costMoney} 
     and arrear_money = #{arrearMoney} 
     and sale_name = #{saleName} 
     and is_sale = #{isSale}

select c.`id`,c.`member_number`,m.`name` as memberName,m.`sex` as sex,m.`phone_1` as phone,c.`sale_number`,c.`cost_type`,c.`shorthand_code`,
c.`cost_name`,c.`cost_money`,c.`arrear_money`,c.`sale_name`,c.`is_sale`,c.`create_time`,c.`type`,c.`original_price`
from cost c join member m on m.card_number = c.member_number

     and c.id = #{id} 
     and c.member_number like concat('%', #{memberNumber},'%') 
     and c.sale_number like concat('%', #{saleNumber},'%') 
     and date_format(c.create_time ,'%Y-%m-%d') = #{createTime} 
     and c.cost_name = #{costName} 
     and m.`name` = #{memberName} 
     and c.cost_money = #{costMoney} 
     and ,m.`phone_1` = #{phone} 
     and c.arrear_money = #{arrearMoney} 
     and c.sale_name = #{saleName} 
    and c.is_sale = 0 and c.type ='检查单'



        order by ${sort} ${order}


        group by c.`sale_number`
        order by c.id desc



    limit #{offset}, #{limit}

select count(*) from cost c join member m on m.card_number = c.member_number

     and c.id = #{id} 
     and c.member_number like concat('%', #{memberNumber},'%') 
     and c.sale_number like concat('%', #{saleNumber},'%') 
     and date_format(c.create_time ,'%Y-%m-%d') = #{createTime} 
     and c.cost_name = #{costName} 
     and m.`name` = #{memberName} 
     and c.cost_money = #{costMoney} 
     and c.arrear_money = #{arrearMoney} 
     and c.sale_name = #{saleName} 
    and c.is_sale = 0 and c.type ='检查单'
