mapper.select:
select gw.`id`,gw.`give_number`,gw.`give_name`,gw.`give_jc`,gw.`stores`,gw.`give_type`,gi.`gift_name` from giveaway gw
left join gift gi on gi.gift_num = gw.give_type

     and gw.id = #{id} 
     and gw.give_number = #{giveNumber} 
     and gw.give_name = #{giveName} 
     and gw.give_jc = #{giveJc} 
     and gw.stores = #{stores} 
     and gw.give_type = #{giveType} 


    
        order by ${sort} ${order}
    
    
        order by id desc
    


    limit #{offset}, #{limit}

select count(*) from giveaway

     and id = #{id} 
     and give_number = #{giveNumber} 
     and give_name = #{giveName} 
     and give_jc = #{giveJc} 
     and stores = #{stores} 
     and give_type = #{giveType}

select p.produc_num,p.produc_name,p.size,u.unitname,p.retail_price from aa_produca p left join c_unit u on u.unitid = p.unitid
and 1=#{num}
UNION
select jc.produc_num,jc.produc_name,'' as size,u.unitname,jc.retail_price from aa_jpcp jc LEFT JOIN c_unit u on u.unitid = jc.unitid
