mapper.select:
select `id`,`member_number`,`sex`,`name`,`call_type`,`call_status`,`consult_room`,`line_time` from line

     and id = #{id} 
     and member_number = #{memberNumber} 
     and sex = #{sex} 
     and name = #{name} 
     and call_type = #{callType} 
    
    
    
        
            and (consult_room = #{consultRoom} and call_status in ('1','2','3') or call_status = '0')
        
        
            and (call_status in ('1','2','3') or call_status = '0')
        
    
     and date_format(line_time ,'%Y-%m-%d') = #{lineTime} 


    
        order by ${sort} ${order}
    
    
        order by line_time ASC
    


    limit #{offset}, #{limit}

        select count(*) from line
        
             and id = #{id} 
             and member_number = #{memberNumber} 
             and sex = #{sex} 
             and name = #{name} 
             and call_type = #{callType} 
             and call_status = #{callStatus} 
            
                
                    and (consult_room = #{consultRoom} and call_status in ('1','2','3') or call_status = '0')
                
                
                    call_status = '0'
                
            

            
             
             and date_format(line_time ,'%Y-%m-%d') = #{lineTime}

select `id`,`member_number`,`sex`,`name`,`call_type`,`call_status`,`consult_room`,`line_time` from line

     and id = #{id} 
     and member_number = #{memberNumber} 
     and sex = #{sex} 
     and name = #{name} 
     and call_type = #{callType} 
    and consult_room = #{consultRoom}
     and date_format(line_time ,'%Y-%m-%d') = #{lineTime} 


    
        order by ${sort} ${order}
    
    
        order by line_time ASC
    


    limit #{offset}, #{limit}

mapper.update:
update line

    `call_status` = #{callStatus}, 

where member_number = #{memberNumber} and date_format(line_time ,'%Y-%m-%d') = #{lineDate}
