mapper.select:
select `id`,`sex`,`member_name`,`call_status`,`consult_room`,`line_time` from ygline

     and id = #{id} 
     and sex = #{sex} 
     and member_name = #{memberName} 
    
    
    
        
            and (consult_room = #{consultRoom} and call_status in ('1','2','3') or call_status = '0')
        
        
            and (call_status in ('1','2','3') or call_status = '0')
        
    
     and date_format(line_time ,'%Y-%m-%d') = #{lineTime} 


    
        order by ${sort} ${order}
    
    
        order by line_time ASC
    


    limit #{offset}, #{limit}

        select count(*) from ygline
        
             and id = #{id} 
             and member_number = #{memberNumber} 
             and sex = #{sex} 
             and member_name = #{memberName} 
             and call_status = #{callStatus} 
            
                
                    and (consult_room = #{consultRoom} and call_status in ('1','2','3') or call_status = '0')
                
                
                   and (call_status in ('1','2','3') or call_status = '0')
                
            

            
             
             and date_format(line_time ,'%Y-%m-%d') = #{lineTime}

select `id`,`sex`,`member_name`,`call_status`,`consult_room`,`line_time` from ygline

     and id = #{id} 
     and sex = #{sex} 
     and member_name = #{memberName} 
    
     and date_format(line_time ,'%Y-%m-%d') = #{lineTime} 
    and (consult_room is null or consult_room ='') and call_status != '4'


    
        order by ${sort} ${order}
    
    
        order by line_time ASC
    


    limit #{offset}, #{limit}

select `id`,`member_name`,`sex`,`consult_room`,`line_time` from ygline_member

     and date_format(line_time ,'%Y-%m-%d') = #{lineTime} 


    
        order by ${sort} ${order}
    
    
        order by id ASC
    


    limit #{offset}, #{limit}
