mapper.select:
select `id`,`sale_number`,`logistic_status` from log_status

     and id = #{id} 
     and sale_number like concat('%', #{saleNumber},'%') 
     and logistic_status = #{logisticStatus} 


    
        order by ${sort} ${order}
    
    
        order by id desc
    


    limit #{offset}, #{limit}

select count(*) from log_status

     and id = #{id} 
     and sale_number like concat('%', #{saleNumber},'%') 
     and logistic_status = #{logisticStatus}

select sa.`id`,sa.`ptometry_number`,sa.`member_number`,m.`name` as memberName,m.`sex`,m.`age`,m.`phone_1` as phone1,sa.`sale_name`,sa.`sale_number`,sa.`eye_type`,sa.`amount_money`,sa.`mirror_way`,sa.`mirror_time`, sa.`miror_address`,sa.`store_num`,sa.`urgent_status`,sa.`goods_rebate`,sa.`fixed_price`,sa.`moling_money`,sa.`additional_cost`,sa.`process_ask`, sa.`store_name`,sa.`store_describe`,sa.`store_unit`,sa.`store_count`,sa.`remark`,sa.`saleremark`,sa.`give_name`,sa.`peijing_time`,sa.`optometry_name`,sa.`out_recipel`, sa.`recipel_type`,sa.`optometrywl_name`,sa.`recipelwl_type`,sa.`taocan_name`,sa.`additional_price`,sa.`additional_count`,sa.`classtype`,ls.`logistic_status` as logStatus,ls.`faliao_name` as faliaoName,ls.`faliao_date` as faliaoDate from store_sales sa join log_status ls on ls.sale_number = sa.sale_number join member m on m.card_number = sa.member_number and m.state = 1 and m.`status` = 0
and ls.logistic_status = #{logisticStatus} and (ls.logistic_status = '委外完成' or ls.logistic_status = '销售完成') and sa.sale_number like concat('%', #{saleNumber},'%') and m.depart_number = #{departNumber} and FIND_IN_SET(m.company_id,#{companyid}) and FIND_IN_SET(#{classtype},sa.classtype) or sa.`store_describe` in (#{storeDescribe})
order by ${sort} ${order}
order by id desc
limit #{offset}, #{limit}

select sa.`id`,sa.`ptometry_number`,sa.`member_number`,m.`name` as memberName,m.`sex`,m.`age`,m.`phone_1` as phone1,sa.`sale_name`,sa.`sale_number`,sa.`eye_type`,sa.`amount_money`,sa.`mirror_way`,sa.`mirror_time`,
sa.`miror_address`,sa.`store_num`,sa.`urgent_status`,sa.`goods_rebate`,sa.`fixed_price`,sa.`moling_money`,sa.`additional_cost`,sa.`process_ask`,
sa.`store_name`,sa.`store_describe`,sa.`store_unit`,sa.`store_count`,sa.`remark`,sa.`saleremark`,sa.`give_name`,sa.`peijing_time`,sa.`optometry_name`,sa.`out_recipel`,
sa.`recipel_type`,sa.`optometrywl_name`,sa.`recipelwl_type`,sa.`taocan_name`,sa.`additional_price`,sa.`additional_count`,ls.`logistic_status` as logStatus,ls.`faliao_name` as faliaoName,ls.`faliao_date` as faliaoDate
from store_sales sa
left join log_status ls on ls.sale_number = sa.sale_number
join member m on m.card_number = sa.member_number and m.state = 1 and m.`status` = 0

     and logistic_status = #{logisticStatus} 
    and sa.sale_number like concat('%', #{saleNumber},'%')
     and m.`name` like concat('%', #{name},'%') 
     and m.phone_1 = #{phone1} 
    and m.depart_number = #{departNumber}
    and m.company_id in (#{companyid})


    
        order by ${sort} ${order}
    
    
        order by id desc
    


    limit #{offset}, #{limit}
