mapper.select:
select m.`id`,m.`name`,m.`member_option`,m.`card_number`,m.`sex`,m.`depart_number`,m.`company_id`,m.`birthday_year`,m.`birthday_month`,
m.`birthday_day`,m.`age`,m.`phone_1`,m.`phone_2`,m.`phone_3`,m.`province_code`,m.`province_name`,m.`city_code`,m.`city_name`,
m.`area_code`,m.`area_name`,m.`card_type`,m.`master_card`,m.`minor_name`,m.`crowd_type`,m.`exclusive_customer`,
m.`exclusive_customer_id`,m.`identity_type`,m.`identity_id`,m.`vocation`,m.`school`,m.`qq_number`,m.`email`,
m.`address`,m.`postal_code`,m.`interest`,m.`source`,m.`integral_pasd`,m.`remark`,m.`register_time`,m.`status`,m.`state`
from member m

     and m.id = #{id} 
     and m.`name` like concat('%', #{name},'%') 
     and m.member_option = #{memberOption} 
    and FIND_IN_SET(m.company_id,#{companyId})
     and m.card_number like concat('%', #{cardNumber},'%') 
     and m.sex = #{sex} 
     and m.birthday_year = #{birthdayYear} 
     and m.birthday_month = #{birthdayMonth} 
     and m.birthday_day = #{birthdayDay} 
     and m.age = #{age} 
     and m.phone_1 = #{phone1} 
     and m.phone_2 = #{phone2} 
     and m.phone_3 = #{phone3} 
     and m.province_code = #{provinceCode} 
     and m.province_name = #{provinceName} 
     and m.city_code = #{cityCode} 
     and m.city_name = #{cityName} 
     and m.area_code = #{areaCode} 
     and m.area_name = #{areaName} 
     and m.card_type = #{cardType} 
     and m.master_card = #{masterCard} 
     and m.minor_name = #{minorName} 
     and m.crowd_type = #{crowdType} 
     and m.exclusive_customer = #{exclusiveCustomer} 
     and m.exclusive_customer_id = #{exclusiveCustomerId} 
     and m.identity_type = #{identityType} 
     and m.identity_id = #{identityId} 
     and m.vocation = #{vocation} 
     and m.school = #{school} 
     and m.qq_number = #{qqNumber} 
     and m.email = #{mail} 
     and m.address = #{address} 
     and m.postal_code = #{postalCode} 
     and m.interest = #{interest} 
     and m.source = #{source} 
     and m.integral_pasd = #{integralPasd} 
     and m.remark = #{remark} 
     and m.register_time = #{registerTime} 
     and m.status = #{status} 
     and m.state = #{state} 
     and m.age >= #{agestart} 
     and m.age <= #{ageend} 



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

select m.`id`,m.`name`,m.`card_number`,c.`id` as costId,c.`is_sale`,c.`type`,c.`sale_number`,m.`sex`,m.`depart_number`,m.`birthday_year`,m.`birthday_month`,m.`birthday_day`,m.`age`,m.`phone_1`,m.`phone_2`,m.`phone_3`,m.`province_code`,m.`province_name`,m.`city_code`,m.`city_name`,m.`area_code`,m.`area_name`,m.`card_type`,m.`master_card`,m.`minor_name`,m.`crowd_type`,m.`exclusive_customer`,m.`exclusive_customer_id`,m.`identity_type`,m.`identity_id`,m.`vocation`,m.`school`,m.`qq_number`,m.`email`,m.`address`,m.`postal_code`,m.`interest`,m.`source`,m.`integral_pasd`,m.`remark`,m.`register_time`,m.`status` from member m
join cost c on c.member_number = m.card_number

     and m.id = #{id} 
     and m.name like concat('%', #{name},'%') 
    and FIND_IN_SET(m.company_id,#{companyId})
     and m.card_number like concat('%', #{cardNumber},'%') 
     and m.sex = #{sex} 
     and m.birthday_year = #{birthdayYear} 
     and m.birthday_month = #{birthdayMonth} 
     and m.birthday_day = #{birthdayDay} 
     and m.age = #{age} 
     and m.phone_1 = #{phone1} 
     and m.phone_2 = #{phone2} 
     and m.phone_3 = #{phone3} 
     and m.state = #{state} 
     and c.is_sale = #{isSale} 



        order by ${sort} ${order}


        group by c.`sale_number`
        order by c.create_time desc



    limit #{offset}, #{limit}

select
count(*) from (
select count(m.id) from member m
