mapper.select:
select b.`status`,b.`state`,
 b.`brandid`,b.`brandnum`,b.`brandname`,b.`goodsid`,good.`goodsname`,b.`materialid`,b.`materialname`,b.`lens_id`,b.`lens_name`,b.`light_id`,
 b.`light_name`,b.`refractivityid`,b.`refractivityvalue`,b.`function_id`,b.`function_name`,b.`gradual_id`,b.`gradual_name`,b.`usage_id`,
 b.`usage_name`,b.`type_id`,b.`type_name`,b.`registration`,b.`mfrsid`,m.`mfrsname`,b.`unitid`,b.`address`,b.`payid`,b.`batchid`,b.`factory`,
 b.`money`,b.`shorthandcode`,b.`productionkey`,b.`payday`
 from c_brand b left join c_mfrs m on b.mfrsid=m.mfrsid left join c_goods good on b.goodsid=good.goodsid

     and b.goodsid = #{goodsid}
     and m.mfrsname = #{mfrsname}
     and b.brandnum = #{brandnum}
     and b.brandname = #{brandname}
     and b.status = #{status}
     and b.payid = #{payid}
     and b.mfrsid LIKE concat('%', #{mfrsids},'%')
     and b.goodsid LIKE concat('%', #{goodsids},'%')
     and  b.state= 1

 group by b.brandid order by b.status asc ,b.brandid asc

select b.`status`,b.`state`,
 b.`brandid`,b.`brandnum`,b.`brandname`,b.`goodsid`,good.`goodsname`,b.`materialid`,b.`materialname`,b.`lens_id`,b.`lens_name`,b.`light_id`,
 b.`light_name`,b.`refractivityid`,b.`refractivityvalue`,b.`function_id`,b.`function_name`,b.`gradual_id`,b.`gradual_name`,b.`usage_id`,
 b.`usage_name`,b.`type_id`,b.`type_name`,b.`registration`,b.`mfrsid`,m.`mfrsname`,b.`unitid`,b.`address`,b.`payid`,b.`batchid`,b.`factory`,
 b.`money`,b.`shorthandcode`,b.`productionkey`,b.`payday`
 from c_brand b left join c_mfrs m on b.mfrsid=m.mfrsid left join c_goods good on b.goodsid=good.goodsid

      and b.goodsid = #{goodsid}
      and m.mfrsname = #{mfrsname}
      and b.brandnum = #{brandnum}
      and b.brandname = #{brandname}
      and b.status = #{status}
      and b.payid = #{payid}
      and b.mfrsid LIKE concat('%', #{mfrsids},'%')
      and b.goodsid LIKE concat('%', #{goodsids},'%')
      and  b.state= 1

 group by b.brandid order by b.status asc ,b.brandid asc

select count(*) from c_brand

    and brandid = #{brandid}
    and brandnum = #{brandnum}
    and brandname = #{brandname}
    and goodsid = #{goodsid}
    and goodsid = #{goodsname}
    and materialid = #{materialid}
    and materialname = #{materialname}
    and lens_id = #{lensId}
    and lens_name = #{lensName}
    and light_id = #{lightId}
    and light_name = #{lightName}
    and refractivityid = #{refractivityid}
    and refractivityvalue =
        #{refractivityvalue}

    and function_id = #{functionId}
    and function_name = #{functionName}
    and gradual_id = #{gradualId}
    and gradual_name = #{gradualName}
    and usage_id = #{usageId}
    and usage_name = #{usageName}
    and type_id = #{typeId}
    and type_name = #{typeName}
    and registration = #{registration}
    and mfrsid = #{mfrsid}
    and mfrsname = #{mfrsname}
    and unitid = #{unitid}
    and address = #{address}
    and payid = #{payid}
    and batchid = #{batchid}
    and factory = #{factory}
    and money = #{money}
    and shorthandcode = #{shorthandcode}
    and productionkey = #{productionkey}
    and status = #{status}
    and state = #{state}
    and payday = #{payday}
