mapper.select:
select `mfrsid`,`mfrsnum`,`mfrsname`,`mfrscontacts`,`mfrsrealname`,`mfrstelephone`,`mfrsphone`,`goodsid`,`mfrsfax`,`mfrsaddress`,`payid`,`invoiceid`,`creditcode`,`creditcodeday`,`medicinecode`,`medicinecodeday`,`productscode`,`productscodeday`,`remarks`,`status` from c_mfrs

	 and mfrsid = #{mfrsid} 
	 and mfrsnum = #{mfrsnum} 
	 and mfrsname = #{mfrsname} 
	 and mfrscontacts = #{mfrscontacts} 
	 and mfrsrealname = #{mfrsrealname} 
	 and mfrstelephone = #{mfrstelephone} 
	 and mfrsphone = #{mfrsphone} 
	 and goodsid = #{goodsid} 
	 and mfrsfax = #{mfrsfax} 
	 and mfrsaddress = #{mfrsaddress} 
	 and payid = #{payid} 
	 and invoiceid = #{invoiceid} 
	 and creditcode = #{creditcode} 
	 and creditcodeday = #{creditcodeday} 
	 and medicinecode = #{medicinecode} 
	 and medicinecodeday = #{medicinecodeday} 
	 and productscode = #{productscode} 
	 and productscodeday = #{productscodeday} 
	 and remarks = #{remarks} 
	 and status = #{status} 



		order by ${sort} ${order}


		order by mfrsid desc



	limit #{offset}, #{limit}

select count(*) from c_mfrs

	 and mfrsid = #{mfrsid} 
	 and mfrsnum = #{mfrsnum} 
	 and mfrsname = #{mfrsname} 
	 and mfrscontacts = #{mfrscontacts} 
	 and mfrsrealname = #{mfrsrealname} 
	 and mfrstelephone = #{mfrstelephone} 
	 and mfrsphone = #{mfrsphone} 
	 and goodsid = #{goodsid} 
	 and mfrsfax = #{mfrsfax} 
	 and mfrsaddress = #{mfrsaddress} 
	 and payid = #{payid} 
	 and invoiceid = #{invoiceid} 
	 and creditcode = #{creditcode} 
	 and creditcodeday = #{creditcodeday} 
	 and medicinecode = #{medicinecode} 
	 and medicinecodeday = #{medicinecodeday} 
	 and productscode = #{productscode} 
	 and productscodeday = #{productscodeday} 
	 and remarks = #{remarks} 
	 and status = #{status}

select m.`mfrsid`,m.`mfrsnum`,m.`mfrsname`,m.`mfrscontacts`,m.`mfrsrealname`,m.`mfrstelephone`,m.`mfrsphone`,m.`goodsid`,m.`mfrsfax`,m.`mfrsaddress`,m.`payid`,m.`invoiceid`,m.`creditcode`,m.`creditcodeday`,m.`medicinecode`,m.`medicinecodeday`,m.`productscode`,m.`productscodeday`,m.`remarks`,m.`status` from c_mfrs m left join cc_mg mg on m.mfrsid=mg.mfrsid

	 and m.mfrsid = #{mfrsid} 
	 and m.mfrsnum = #{mfrsnum} 
	 and m.mfrsname = #{mfrsname} 
	 and m.mfrscontacts = #{mfrscontacts} 
	 and m.mfrsrealname = #{mfrsrealname} 
	 and m.mfrstelephone = #{mfrstelephone} 
	 and m.mfrsphone = #{mfrsphone} 
	 and mg.goodsid = #{goodsid} 
	 and m.mfrsfax = #{mfrsfax} 
	 and m.mfrsaddress = #{mfrsaddress} 
	 and m.payid = #{payid} 
	 and m.invoiceid = #{invoiceid} 
	 and m.creditcode = #{creditcode} 
	 and m.creditcodeday = #{creditcodeday} 
	 and m.medicinecode = #{medicinecode} 
	 and m.medicinecodeday = #{medicinecodeday} 
	 and m.productscode = #{productscode} 
	 and m.productscodeday = #{productscodeday} 
	 and m.remarks = #{remarks} 
	 and m.status = #{status} 




		 order by ${sort} ${order}


		group by m.mfrsid
		order by m.mfrsid desc



	limit #{offset}, #{limit}

