select m.`id`,m.`name`,m.`member_option`,m.`card_number`,m.`sex`,m.`depart_number`,m.`birthday_year`,m.`birthday_month`, m.`birthday_day`,m.`age`,m.`phone_1`,m.`phone_2`,m.`phone_3`,m.`province_code`,m.`province_name`,m.`city_code`, m.`city_name`,m.`area_code`,m.`area_name`,m.`card_type`,m.`master_card`,m.`minor_name`,m.`crowd_type`,m.`exclusive_customer`, m.`exclusive_customer_id`,m.`identity_type`,m.`identity_id`,m.`vocation`,m.`school`,m.`qq_number`,m.`email`,`address`, m.`postal_code`,m.`interest`,m.`source`,m.`integral_pasd`,m.`remark`,m.`register_time`,m.`status`,m.`state` from member m join department d on d.depart_number = m.depart_number
and m.card_number like concat('%', #{cardNumber},'%') and m.`name` like concat('%', #{name},'%') and m.phone_1 = #{phone1} and DATE_FORMAT(c.create_time,'%Y-%m-%d') = #{dateNow}
and m.depart_number = ${departNumber}
and FIND_IN_SET(d.company_id,#{companyId}) and m.`status` = 0 and m.state = 1

select count(*)
from member m join department d on d.depart_number = m.depart_number

     and card_number like concat('%', #{cardNumber},'%') 
     and m.`name` like concat('%', #{name},'%') 
     and m.phone_1 = #{phone1} 
     and DATE_FORMAT(c.create_time,'%Y-%m-%d') = #{dateNow} 

        and m.depart_number = ${departNumber}

    and FIND_IN_SET(d.company_id,#{companyId})
    and m.`status` = 0
    and m.state = 1

select
`id`,`member_inumber`,`mydriasis`,`sphere_right`,`sphere_left`,`cylinder_right`,`cylinder_left`,`axial_right`,`axial_left`,
`corneal_astigmatism_os`,`corneal_astigmatism_od`,`sph_jyod`,`sph_jyos`,`cyl_jyod`,`cyl_jyos`,`axial_jyod`,
`axial_jyos`,`va_jyod`,`va_jyos`,`pd_jyod`,`pd_jyos`,`sph_cpod`,`sph_cpos`,`cyl_cpod`,`cyl_cpos`,`axial_cpod`,`axial_cpos`,
`dva_cpod`,`dva_cpos`,`nva_cpod`,`nva_cpos`,`add_cpod`,`add_cpos`,`distance_od`,`distance_os`,`create_time`
from optometry

    and id = #{id}
    and member_inumber = #{memberInumber}
    and mydriasis = #{mydriasis}
    and sphere_right = #{sphereRight}
    and sphere_left = #{sphereLeft}
    and cylinder_right = #{cylinderRight}
    and cylinder_left = #{cylinderLeft}
    and axial_right = #{axialRight}
    and axial_left = #{axialLeft}
    and corneal_astigmatism_os =
        #{cornealAstigmatismOs}

    and corneal_astigmatism_od =
        #{cornealAstigmatismOd}

    and sph_jyod = #{sphJyod}
    and sph_jyos = #{sphJyos}
    and cyl_jyod = #{cylJyod}
    and cyl_jyos = #{cylJyos}
    and axial_jyod = #{axialJyod}
    and axial_jyos = #{axialJyos}
    and va_jyod = #{vaJyod}
    and va_jyos = #{vaJyos}
    and pd_jyod = #{pdJyod}
    and pd_jyos = #{pdJyos}
    and sph_cpod = #{sphCpod}
    and sph_cpos = #{sphCpos}
    and cyl_cpod = #{cylCpod}
    and cyl_cpos = #{cylCpos}
    and axial_cpod = #{axialCpod}
    and axial_cpos = #{axialCpos}
    and dva_cpod = #{dvaCpod}
    and dva_cpos = #{dvaCpos}
    and nva_cpod = #{nvaCpod}
    and nva_cpos = #{nvaCpos}
    and add_cpod = #{addCpod}
    and add_cpos = #{addCpos}
    and distance_od = #{distanceOd}
    and distance_os = #{distanceOs}

order by create_time desc

