select
`id`,`member_inumber`,`mydriasis`,`pharmacy_id`,`pharmacy`,`sphere_right`,`sphere_left`,`cylinder_right`,`cylinder_left`,`axial_right`,`axial_left`,`corneal_astigmatism_os`,`corneal_astigmatism_od`,`sph_jyod`,`sph_jyos`,`cyl_jyod`,`cyl_jyos`,`axial_jyod`,`axial_jyos`,`va_jyod`,`va_jyos`,`pd_jyod`,`pd_jyos`,`sph_cpod`,`sph_cpos`,`cyl_cpod`,`cyl_cpos`,`axial_cpod`,`axial_cpos`,`dva_cpod`,`dva_cpos`,`nva_cpod`,`nva_cpos`,`add_cpod`,`add_cpos`,`distance_od`,`distance_os`
from optometry

    and id = #{id}
    and member_inumber = #{memberInumber}
    and mydriasis = #{mydriasis}
    and pharmacy_id = #{pharmacyId}
    and pharmacy = #{pharmacy}
    and sphere_right = #{sphereRight}
    and sphere_left = #{sphereLeft}
    and cylinder_right = #{cylinderRight}
    and cylinder_left = #{cylinderLeft}
    and axial_right = #{axialRight}
    and axial_left = #{axialLeft}
    and corneal_astigmatism_os =
        #{cornealAstigmatismOs}

    and corneal_astigmatism_od =
        #{cornealAstigmatismOd}

    and sph_jyod = #{sphJyod}
    and sph_jyos = #{sphJyos}
    and cyl_jyod = #{cylJyod}
    and cyl_jyos = #{cylJyos}
    and axial_jyod = #{axialJyod}
    and axial_jyos = #{axialJyos}
    and va_jyod = #{vaJyod}
    and va_jyos = #{vaJyos}
    and pd_jyod = #{pdJyod}
    and pd_jyos = #{pdJyos}
    and sph_cpod = #{sphCpod}
    and sph_cpos = #{sphCpos}
    and cyl_cpod = #{cylCpod}
    and cyl_cpos = #{cylCpos}
    and axial_cpod = #{axialCpod}
    and axial_cpos = #{axialCpos}
    and dva_cpod = #{dvaCpod}
    and dva_cpos = #{dvaCpos}
    and nva_cpod = #{nvaCpod}
    and nva_cpos = #{nvaCpos}
    and add_cpod = #{addCpod}
    and add_cpos = #{addCpos}
    and distance_od = #{distanceOd}
    and distance_os = #{distanceOs}



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

