    and cyl_jyod = #{cylJyod}
    and cyl_jyos = #{cylJyos}
    and axial_jyod = #{axialJyod}
    and axial_jyos = #{axialJyos}
    and va_jyod = #{vaJyod}
    and va_jyos = #{vaJyos}
    and pd_jyod = #{pdJyod}
    and pd_jyos = #{pdJyos}
    and sph_cpod = #{sphCpod}
    and sph_cpos = #{sphCpos}
    and cyl_cpod = #{cylCpod}
    and cyl_cpos = #{cylCpos}
    and axial_cpod = #{axialCpod}
    and axial_cpos = #{axialCpos}
    and dva_cpod = #{dvaCpod}
