mapper.select:
		select p.`id`,p.`package_id`,p.`package_name`,p.`package_time`,p.`package_form`,p.`effective_date`,p.`expiry_date`,
		p.`package_type`,p.`start_price`,p.`end_price`,p.`package_price`,p.`is_cumulative_points`,p.`is_add`,p.`fold_up`,
		p.`retail_price`,p.`store`,p.`store_num`,p.`remark`,p.`whole_start`,p.`whole_end`,p.`danyiyh`,p.`dazhe_price`,
		p.`fanxian_price`,p.`tejia_price`,
		pi.`goods_type`,pi.`goods_code`,pi.`goods_name`,pi.`buy_way`,pi.`package_start_price`,pi.`package_end_price`,
		pi.`sale_number`,pi.`full_start_price`,pi.`full_end_price`,pi.`preferential_way`,pi.`unit_price`,pi.`special_price`,
		pi.`preferential_price`,pi.`preferential_rebate`
		from package p
		left join package_info pi on pi.package_id = p.package_id
          
		  		   and p.id = #{id} 
		  		   and p.package_name = #{packageName} 
		  		   and p.package_time = #{packageTime} 
		  		   and p.package_form = #{packageForm} 
		  		   and p.effective_date = #{effectiveDate} 
		  		   and p.expiry_date = #{expiryDate} 
		  		   and p.package_type = #{packageType} 
		  		   and p.start_price = #{startPrice} 
		  		   and p.end_price = #{endPrice} 
		  		   and p.package_price = #{packagePrice} 
		  		   and p.is_cumulative_points = #{isCumulativePoints} 
		  		   and p.is_add = #{isAdd} 
		  		   and p.fold_up = #{foldUp} 
		  		   and p.retail_price = #{retailPrice} 
		  		   and p.store = #{store} 
			       and FIND_IN_SET(#{storeNum},p.`store_num`) 
		  		   and p.remark = #{remark} 
			      and (pi.goods_code in (#{goodsCode}) or pi.goods_code is NULL or pi.goods_code = '') 
		         
			     
		  		
        
            
                order by ${sort} ${order}
            
			
                order by id desc
			
        
		
			limit #{offset}, #{limit}

	select count(*)
	from package p
	left join package_info pi on pi.package_id = p.package_id
	
		 and p.id = #{id} 
		 and p.package_name = #{packageName} 
		 and p.package_time = #{packageTime} 
		 and p.package_form = #{packageForm} 
		 and p.effective_date = #{effectiveDate} 
		 and p.expiry_date = #{expiryDate} 
		 and p.package_type = #{packageType} 
		 and p.start_price = #{startPrice} 
		 and p.end_price = #{endPrice} 
		 and p.package_price = #{packagePrice} 
		 and p.is_cumulative_points = #{isCumulativePoints} 
		 and p.is_add = #{isAdd} 
		 and p.fold_up = #{foldUp} 
		 and p.retail_price = #{retailPrice} 
		 and p.store = #{store} 
		 and FIND_IN_SET(#{storeNum},p.`store_num`) 
		 and p.remark = #{remark} 
		 and (pi.goods_code in(#{goodsCode}) or pi.goods_code is NULL )
