mapper.select:
select
m.mfrsname,b.brandname,u.unitname,
h.`id`,h.`produc_num`,h.`produc_code`,h.`produc_name`,h.`mfrsid`,h.`brandid`,h.`factory`,h.`produc_factory`,
h.`proid`,h.`big`,h.`small`,h.`unitid`,h.`batchid`,h.`tax`,h.`tax_price`,h.`trade_price`,h.`transfer_price`,h.`retail_price`
from aa_hc h left join c_mfrs m on h.mfrsid=m.mfrsid left join c_brand b on h.brandid=b.brandid left join
c_unit u on h.unitid= u.unitid

    and h.id = #{id}
    and h.produc_num = #{producNum}
    and h.produc_code = #{producCode}
    and h.produc_name = #{producName}
    and h.mfrsid = #{mfrsid}
    and h.brandid = #{brandid}
    and h.factory = #{factory}
    and h.produc_factory = #{producFactory}
    and h.proid = #{proid}
    and h.big = #{big}
    and h.small = #{small}
    and h.unitid = #{unitid}
    and h.batchid = #{batchid}
    and h.tax = #{tax}
    and h.tax_price = #{taxPrice}
    and h.trade_price = #{tradePrice}
    and h.transfer_price = #{transferPrice}
    and h.retail_price = #{retailPrice}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}
    and u.unitname = #{unitname}



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

select count(*) from aa_hc

    and id = #{id}
    and produc_num = #{producNum}
    and produc_code = #{producCode}
    and produc_name = #{producName}
    and mfrsid = #{mfrsid}
    and brandid = #{brandid}
    and factory = #{factory}
    and produc_factory = #{producFactory}
    and proid = #{proid}
    and big = #{big}
    and small = #{small}
    and unitid = #{unitid}
    and batchid = #{batchid}
    and tax = #{tax}
    and tax_price = #{taxPrice}
    and trade_price = #{tradePrice}
    and transfer_price = #{transferPrice}
    and retail_price = #{retailPrice}
