mapper.select:
select
m.mfrsname,b.brandname,u.unitname,
h.`id`,h.`produc_num`,h.`produc_code`,h.`produc_name`,h.`mfrsid`,h.`brandid`,h.`brandnum`,h.`factory`,h.`produc_factory`,
h.`proid`,h.`big`,h.`small`,h.`unitid`,h.`batchid`,h.`tax`,h.`tax_price`,h.`trade_price`,h.`transfer_price`,
h.`retail_price`,h.`status`,h.`state`,h.`smallstr`
from aa_hc h left join c_mfrs m on h.mfrsid=m.mfrsid left join c_brand b on h.brandnum=b.brandnum left join
c_unit u on h.unitid= u.unitid

    and h.produc_num = #{producNum}
    and h.produc_name = #{producName}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}

        and h.retail_price between #{retailPrice,jdbcType=VARCHAR} and #{retailPrice2,jdbcType=VARCHAR}


        and h.tax_price between #{taxPrice,jdbcType=VARCHAR} and #{taxPrice2,jdbcType=VARCHAR}

    and h.big = #{big}
    and h.status = #{status}
    and  h.state= 1

group by h.id order by h.status asc ,h.id asc

select count(*) from aa_hc

    and id = #{id}
    and produc_num = #{producNum}
    and produc_code = #{producCode}
    and produc_name = #{producName}
    and mfrsid = #{mfrsid}
    and brandid = #{brandid}
    and brandnum = #{brandnum}
    and factory = #{factory}
    and produc_factory = #{producFactory}
    and proid = #{proid}
    and big = #{big}
    and small = #{small}
    and smallstr = #{smallstr}
    and unitid = #{unitid}
    and batchid = #{batchid}
    and tax = #{tax}
    and tax_price = #{taxPrice}
    and trade_price = #{tradePrice}
    and transfer_price = #{transferPrice}
    and retail_price = #{retailPrice}
    and status = #{status}
    and state = #{state}
