mapper.select:
select m.`mfrsname`,b.`brandname`,u.`unitname`,
h.`id`,h.`produc_num`,h.`produc_code`,h.`produc_name`,h.`mfrsid`,h.`brandid`,h.`factory`,h.`proid`,h.`unitid`,h.`product_type`,h.`main_capacity`,
h.`second_capacity`,h.`produc_factory`,h.`upday`,h.`downday`,h.`tax`,h.`tax_price`,h.`trade_price`,h.`transfer_price`,h.`retail_price`
from aa_hly h left join c_mfrs m on h.mfrsid=m.mfrsid left join c_brand b on h.brandid=b.brandid
left join c_unit u on h.unitid= u.unitid

    and h.id = #{id}
    and h.produc_num = #{producNum}
    and h.produc_code = #{producCode}
    and h.produc_name = #{producName}
    and h.mfrsid = #{mfrsid}
    and h.brandid = #{brandid}
    and h.factory = #{factory}
    and h.proid = #{proid}
    and h.unitid = #{unitid}
    and h.product_type = #{productType}
    and h.main_capacity = #{mainCapacity}
    and h.second_capacity = #{secondCapacity}
    and h.produc_factory = #{producFactory}
    and h.upday = #{upday}
    and h.downday = #{downday}
    and h.tax = #{tax}
    and h.tax_price = #{taxPrice}
    and h.trade_price = #{tradePrice}
    and h.transfer_price = #{transferPrice}
    and h.retail_price = #{retailPrice}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}
    and u.unitname = #{unitname}



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

select
`id`,`produc_num`,`produc_code`,`produc_name`,`mfrsid`,`brandid`,`factory`,`proid`,`unitid`,`product_type`,`main_capacity`,`second_capacity`,`produc_factory`,`upday`,`downday`,`tax`,`tax_price`,`trade_price`,`transfer_price`,`retail_price`
from aa_hly

    and id = #{id}
    and produc_num = #{producNum}
    and produc_code = #{producCode}
    and produc_name = #{producName}
    and mfrsid = #{mfrsid}
    and brandid = #{brandid}
    and factory = #{factory}
    and proid = #{proid}
    and unitid = #{unitid}
    and product_type = #{productType}
    and main_capacity = #{mainCapacity}
    and second_capacity = #{secondCapacity}
    and produc_factory = #{producFactory}
    and upday = #{upday}
    and downday = #{downday}
    and tax = #{tax}
    and tax_price = #{taxPrice}
    and trade_price = #{tradePrice}
    and transfer_price = #{transferPrice}
    and retail_price = #{retailPrice}



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

