mapper.select:
select m.`mfrsname`,b.`brandname`,u.`unitname`,
h.`id`,h.`produc_num`,h.`produc_code`,h.`produc_name`,h.`mfrsid`,h.`brandid`,h.`brandnum`,h.`factory`,h.`proid`,h.`unitid`,h.`product_type`,h.`main_capacity`,
h.`second_capacity`,h.`produc_factory`,h.`upday`,h.`downday`,h.`tax`,h.`tax_price`,h.`trade_price`,h.`transfer_price`,h.`retail_price`,
h.`xsstate`,h.`state`
from aa_hly h left join c_mfrs m on h.mfrsid=m.mfrsid left join c_brand b on h.brandnum=b.brandnum
left join c_unit u on h.unitid= u.unitid

    and h.produc_num = #{producNum}
    and h.produc_name = #{producName}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}

        and   #{retailPrice} <=  h.retail_price and h.retail_price <=  #{retailPrice2}


        and   #{taxPrice} <=  h.tax_price and h.retail_price <=  #{taxPrice2}

    and h.xsstate = #{xsstate}
    and  h.state= 1

group by h.id order by  h.id desc

select count(*) from aa_hly h left join c_mfrs m on h.mfrsid=m.mfrsid left join c_brand b on h.brandnum=b.brandnum
left join c_unit u on h.unitid= u.unitid

    and h.produc_num = #{producNum}
    and h.produc_name = #{producName}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}

        and   #{retailPrice} <=  h.retail_price and h.retail_price <=  #{retailPrice2}


        and   #{taxPrice} <=  h.tax_price and h.retail_price <=  #{taxPrice2}

    and h.xsstate = #{xsstate}
    and  h.state= 1

select
h.`id`,h.`produc_num`,h.`produc_code`,h.`produc_name`,h.`mfrsid`,h.`brandid`,h.`brandnum`,b.`brandname`,
h.`factory`,h.`proid`,h.`unitid`,u.unitname,h.`product_type`,h.`main_capacity`,h.`second_capacity`,h.`produc_factory`,
h.`upday`,h.`downday`,h.`tax`,h.`tax_price`,h.`trade_price`,h.`transfer_price`,h.`retail_price`,h.`xsstate`,h.`state`
FROM aa_hly h LEFT JOIN c_brand b ON h.brandnum = b.brandnum LEFT JOIN c_unit u ON h.unitid = u.unitid

    and h.id = #{id}
    and h.produc_num = #{producNum}
    and h.produc_code = #{producCode}
    and h.produc_name = #{producName}
    and h.mfrsid = #{mfrsid}
    and h.brandid = #{brandid}
    and h.brandnum = #{brandnum}
    and h.factory = #{factory}
    and h.proid = #{proid}
    and h.unitid = #{unitid}
    and h.product_type = #{productType}
    and h.main_capacity = #{mainCapacity}
    and h.second_capacity = #{secondCapacity}
    and h.produc_factory = #{producFactory}
    and h.upday = #{upday}
    and h.downday = #{downday}
    and h.tax = #{tax}
    and h.tax_price = #{taxPrice}
    and h.trade_price = #{tradePrice}
    and h.transfer_price = #{transferPrice}
    and h.retail_price = #{retailPrice}
    and h.xsstate = #{xsstate}
   and h.state = 1



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

select count(*)  FROM aa_hly h LEFT JOIN c_brand b ON h.brandnum = b.brandnum LEFT JOIN c_unit u ON h.unitid = u.unitid

    and h.id = #{id}
    and h.produc_num = #{producNum}
    and h.produc_code = #{producCode}
    and h.produc_name = #{producName}
    and h.mfrsid = #{mfrsid}
    and h.brandid = #{brandid}
    and h.brandnum = #{brandnum}
    and h.factory = #{factory}
    and h.proid = #{proid}
    and h.unitid = #{unitid}
    and h.product_type = #{productType}
    and h.main_capacity = #{mainCapacity}
    and h.second_capacity = #{secondCapacity}
    and h.produc_factory = #{producFactory}
    and h.upday = #{upday}
    and h.downday = #{downday}
    and h.tax = #{tax}
    and h.tax_price = #{taxPrice}
    and h.trade_price = #{tradePrice}
    and h.transfer_price = #{transferPrice}
    and h.retail_price = #{retailPrice}
    and h.xsstate = #{xsstate}
    and h.state = 1
