mapper.select:
select
j.`id`,j.`produc_num`,j.`produc_code`,j.`produc_name`,j.`mfrsid`,j.`brandid`,j.`proid`,j.`unitid`,j.`sph_id`,j.`cyl_id`,
j.`lightbelow_id`,j.`lens_id`,j.`batchid`,j.`light_id`,j.`gradual_id`,j.`refractivityid`,j.`function_id`,j.`tax`,j.`tax_price`,
j.`trade_price`,j.`transfer_price`,j.`retail_price` ,
m.mfrsname,b.brandname,u.unitname
from aa_jpcp j left join c_mfrs m on j.mfrsid=m.mfrsid left join c_brand b on j.brandid=b.brandid left join
c_unit u on j.unitid= u.unitid

    and j.id = #{id}
    and j.produc_num = #{producNum}
    and j.produc_code = #{producCode}
    and j.produc_name = #{producName}
    and j.mfrsid = #{mfrsid}
    and j.brandid = #{brandid}
    and j.proid = #{proid}
    and j.unitid = #{unitid}
    and j.sph_id = #{sphId}
    and j.cyl_id = #{cylId}
    and j.lightbelow_id = #{lightbelowId}
    and j.lens_id = #{lensId}
    and j.batchid = #{batchid}
    and j.light_id = #{lightId}
    and j.gradual_id = #{gradualId}
    and j.refractivityid = #{refractivityid}
    and j.function_id = #{functionId}
    and j.tax = #{tax}
    and j.tax_price = #{taxPrice}
    and j.trade_price = #{tradePrice}
    and j.transfer_price = #{transferPrice}
    and j.retail_price = #{retailPrice}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}
    and u.unitname = #{unitname}



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

select count(*) from aa_jpcp

    and id = #{id}
    and produc_num = #{producNum}
    and produc_code = #{producCode}
    and produc_name = #{producName}
    and mfrsid = #{mfrsid}
    and brandid = #{brandid}
    and proid = #{proid}
    and unitid = #{unitid}
    and sph_id = #{sphId}
    and cyl_id = #{cylId}
    and lightbelow_id = #{lightbelowId}
    and lens_id = #{lensId}
    and batchid = #{batchid}
    and light_id = #{lightId}
    and gradual_id = #{gradualId}
    and refractivityid = #{refractivityid}
    and function_id = #{functionId}
    and tax = #{tax}
    and tax_price = #{taxPrice}
    and trade_price = #{tradePrice}
    and transfer_price = #{transferPrice}
    and retail_price = #{retailPrice}
