select lig.light_name, yl.cyl, cyl.cyl, ph.sph, sph.sph, lt.lightbelow, ltb.lightbelow, m.mfrsname,b.brandname,u.unitnum, j.`id`,j.`produc_num`,j.`produc_code`,j.`produc_name`,j.`mfrsid`,j.`brandid`,j.`brandnum`,j.`factory`,j.`produc_factory`, j.`proid`,j.`unitid`,j.`sph_id`,j.`span_id`,j.`cyl_id`,j.`sph_up`,j.`sph_down`,j.`sph_span`,j.`cyl_up`,j.`cyl_down`, j.`cyl_span`,j.`lightbelow_id`,j.`lightbelow_right`,j.`lightbelow_left`,j.`lens_id`,l.`lens_name`,j.`cycle`,j.`light_id`,pl.`light_name`, j.`gradual_id`,g.`gradual_name`,j.`refractivityid`,r.`refractivityvalue`,j.`function_id`,f.`function_name`,j.`combined_photometry`,j.`can`,j.`tax`,j.`tax_price`, j.`trade_price`,j.`transfer_price`,j.`retail_price`,j.`xsstate`,j.`state`,j.`classtype` from aa_jpdz j left join c_refractivity r on r.refractivityid = j.refractivityid left join p_light pl on pl.light_id = j.light_id left join p_lens l on l.lens_id = j.lens_id left join p_gradual g on g.gradual_id = j.gradual_id left join p_function f on f.function_id = j.function_id left join c_mfrs m on j.mfrsid=m.mfrsnum left join c_brand b on j.brandnum=b.brandnum AND j.`mfrsid`=b.`mfrsid` left join c_unit u on u.unitid=j.unitid left join a_cyl yl on yl.cyl_id=j.cyl_up left join a_cyl cyl on cyl.cyl_id=j.cyl_down left join a_sph ph on ph.sph_id=j.sph_up left join a_sph sph on sph.sph_id=j.sph_down left join a_lightbelow lt on lt.lightbelow_id=j.lightbelow_left left join a_lightbelow ltb on ltb.lightbelow_id=j.lightbelow_right left join p_light lig on lig.light_id=j.light_id
and j.produc_num LIKE concat(concat('%', #{producNum}),'%') and j.produc_name LIKE concat(concat('%', #{producName}),'%') and j.produc_code LIKE concat(concat('%', #{goodsCode}),'%') and m.mfrsname LIKE concat(concat('%', #{mfrsname}),'%') and b.brandname LIKE concat(concat('%', #{brandname}),'%')
and #{retailPrice} <= j.retail_price and j.retail_price <= #{retailPrice2}
and #{taxPrice} <= j.tax_price and j.retail_price <= #{taxPrice2}
and j.lens_id = #{lensId} and j.refractivityid = #{refractivityid} and j.light_id = #{lightId} and j.xsstate = #{xsstate} and j.classtype = #{classtype} and j.state= 1
order by ${sort} ${order}
order by j.id desc
limit #{offset}, #{limit}

select count(*) from aa_jpdz j left join c_refractivity r on r.refractivityid = j.refractivityid left join p_light pl on pl.light_id = j.light_id left join p_lens l on l.lens_id = j.lens_id left join p_gradual g on g.gradual_id = j.gradual_id left join p_function f on f.function_id = j.function_id left join c_mfrs m on j.mfrsid=m.mfrsnum left join c_brand b on j.brandnum=b.brandnum AND j.`mfrsid`=b.`mfrsid` left join c_unit u on u.unitid=j.unitid left join a_cyl yl on yl.cyl_id=j.cyl_up left join a_cyl cyl on cyl.cyl_id=j.cyl_down left join a_sph ph on ph.sph_id=j.sph_up left join a_sph sph on sph.sph_id=j.sph_down left join a_lightbelow lt on lt.lightbelow_id=j.lightbelow_left left join a_lightbelow ltb on ltb.lightbelow_id=j.lightbelow_right left join p_light lig on lig.light_id=j.light_id
and j.produc_num LIKE concat(concat('%', #{producNum}),'%') and j.produc_name LIKE concat(concat('%', #{producName}),'%') and j.produc_code LIKE concat(concat('%', #{goodsCode}),'%') and m.mfrsname LIKE concat(concat('%', #{mfrsname}),'%') and b.brandname LIKE concat(concat('%', #{brandname}),'%')
and #{retailPrice} <= j.retail_price and j.retail_price <= #{retailPrice2}
and #{taxPrice} <= j.tax_price and j.retail_price <= #{taxPrice2}
and j.lens_id = #{lensId} and j.refractivityid = #{refractivityid} and j.light_id = #{lightId} and j.xsstate = #{xsstate} and j.classtype = #{classtype} and j.state= 1

select
yl.cyl,
cyl.cyl,
ph.sph,
sph.sph,
lt.lightbelow,
ltb.lightbelow,
m.mfrsname,b.brandname,u.unitnum,
j.`id`,j.`produc_num`,j.`produc_code`,j.`produc_name`,j.`mfrsid`,j.`brandid`,j.`brandnum`,j.`factory`,j.`produc_factory`,
j.`proid`,j.`unitid`,j.`sph_id`,j.`span_id`,j.`cyl_id`,j.`sph_up`,j.`sph_down`,j.`sph_span`,j.`cyl_up`,j.`cyl_down`,
j.`cyl_span`,j.`lightbelow_id`,j.`lightbelow_right`,j.`lightbelow_left`,j.`lens_id`,l.`lens_name`,j.`cycle`,j.`light_id`,pl.`light_name` as lightName,
j.`gradual_id`,g.`gradual_name`,j.`refractivityid`,r.`refractivityvalue`,j.`function_id`,f.`function_name`,j.`combined_photometry`,j.`can`,j.`tax`,j.`tax_price`,
j.`trade_price`,j.`transfer_price`,j.`retail_price`
from aa_jpdz j
left join c_refractivity r on r.refractivityid = j.refractivityid
left join p_light pl on pl.light_id = j.light_id
left join p_lens l on l.lens_id = j.lens_id
left join p_gradual g on g.gradual_id = j.gradual_id
left join p_function f on f.function_id = j.function_id
left join c_mfrs m on j.mfrsid=m.mfrsnum
left join c_brand b on j.brandnum=b.brandnum
left join c_unit u on u.unitid=j.unitid
left join a_cyl yl on yl.cyl_id=j.cyl_up
left join a_cyl cyl on cyl.cyl_id=j.cyl_down
left join a_sph ph on ph.sph_id=j.sph_up
left join a_sph sph on sph.sph_id=j.sph_down
left join a_lightbelow lt on lt.lightbelow_id=j.lightbelow_id
left join a_lightbelow ltb on ltb.lightbelow_id=j.lightbelow_right

    and j.id = #{id}
    and j.produc_num = #{producNum}
    and j.produc_code = #{producCode}
    and j.produc_code = #{goodsCode}
    and j.produc_name = #{producName}
    and j.mfrsid = #{mfrsid}
    and j.brandid = #{brandid}
    and j.brandnum = #{brandnum}
    and j.factory = #{factory}
    and j.produc_factory = #{producFactory}
    and j.proid = #{proid}
    and j.unitid = #{unitid}
    and j.sph_id = #{sphId}
    and j.span_id = #{spanId}
    and j.cyl_id = #{cylId}
    and j.sph_up = #{sphUp}
    and j.sph_down = #{sphDown}
    and j.sph_span = #{sphSpan}
    and j.cyl_up = #{cylUp}
    and j.cyl_down = #{cylDown}
    and j.cyl_span = #{cylSpan}
    and j.lightbelow_id = #{lightbelowId}
    and j.lightbelow_right = #{lightbelowRight}

    and j.lightbelow_left = #{lightbelowLeft}
    and j.lens_id = #{lensId}
    and j.cycle = #{cycle}
    and j.light_id = #{lightId}
    and j.gradual_id = #{gradualId}
    and j.refractivityid = #{refractivityid}
    and j.function_id = #{functionId}
    and j.combined_photometry
        =#{combinedPhotometry}

    and j.can = #{can}
    and j.tax = #{tax}
    and j.tax_price = #{taxPrice}
    and j.trade_price = #{tradePrice}
    and j.transfer_price = #{transferPrice}
    and j.retail_price = #{retailPrice}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}
    and u.unitname = #{unitname}




        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}
