mapper.select:
select
m.`mfrsname`,b.`brandname`,u.`unitname`,o.`id`,o.`produc_num`,o.`produc_code`,o.`produc_name`,o.`mfrsid`,o.`brandnum`,o.`brandid`,o.`factory`,o.`produc_factory`,
o.`produc_factorycolor`,o.`unitid`,o.`old_id`,o.`batchid`,o.`size`,o.`color`,o.`style_id`,o.`tax`,o.`tax_price`,o.`trade_price`,
o.`transfer_price`,o.`retail_price`,o.`xsstate`,o.`state`,o.`classtype`,o.`view_good_name`
from aa_oldlens o left join c_mfrs m on o.mfrsid=m.mfrsnum left join c_brand b on o.brandnum=b.brandnum AND o.`mfrsid`=b.`mfrsid`
left join c_unit u on o.unitid= u.unitid

    and o.produc_num  LIKE concat(concat('%', #{producNum}),'%')
    and o.produc_code  LIKE concat(concat('%', #{goodsCode}),'%')

    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}
    and o.produc_name LIKE concat(concat('%', #{producName}),'%')


    and ABS(o.retail_price) >= ABS(#{retailPrice})
    and ABS(o.retail_price) <= ABS(#{retailPrice2})
    and ABS(o.tax_price) >= ABS(#{taxPrice})
    and ABS(o.tax_price) <= ABS(#{taxPrice2})

    and o.produc_factory = #{producFactory}
    and o.produc_color = #{producColor}
    and o.xsstate = #{xsstate}
    and o.view_good_name = #{viewGoodName}

    and o.produc_num LIKE concat(concat('%', #{num}),'%')
    and o.retail_price = #{oldMoney}
    and  o.state= 1 and m.xsstate=0 and m.state=1 and b.xsstate=0 and b.state=1




        order by ${sort} ${order}


          order by  o.id desc



    limit #{offset}, #{limit}

select count(*)   from aa_oldlens o left join c_mfrs m on o.mfrsid=m.mfrsnum left join c_brand b on o.brandnum=b.brandnum AND o.`mfrsid`=b.`mfrsid`
left join c_unit u on o.unitid= u.unitid

    and o.produc_num  LIKE concat(concat('%', #{producNum}),'%')
    and o.produc_code  LIKE concat(concat('%', #{goodsCode}),'%')

    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}
    and o.produc_name LIKE concat(concat('%', #{producName}),'%')

    and ABS(o.retail_price) >= ABS(#{retailPrice})
    and ABS(o.retail_price) <= ABS(#{retailPrice2})
    and ABS(o.tax_price) >= ABS(#{taxPrice})
    and ABS(o.tax_price) <= ABS(#{taxPrice2})

    and o.produc_factory = #{producFactory}
    and o.produc_color = #{producColor}
    and o.xsstate = #{xsstate}
    and o.view_good_name = #{viewGoodName}


    and o.produc_num LIKE concat(concat('%', #{num}),'%')
    and o.retail_price = #{oldMoney}
    and  o.state= 1 and m.xsstate=0 and m.state=1 and b.xsstate=0 and b.state=1

mapper.update:
update aa_oldlens
`produc_num` = #{producNum}, `produc_code` = #{producCode}, `produc_name` = #{producName}, `mfrsid` = #{mfrsid}, `brandid` = #{brandid}, `brandnum` = #{brandnum}, `factory` = #{factory}, `produc_factory` = #{producFactory}, `produc_factorycolor` = #{producFactorycolor}, `unitid` = #{unitid}, `old_id` = #{oldId}, `batchid` = #{batchid}, `size` = #{size}, `color` = #{color}, `style_id` = #{styleId}, `tax` = #{tax}, `tax_price` = #{taxPrice}, `trade_price` = #{tradePrice}, `transfer_price` = #{transferPrice}, `retail_price` = #{retailPrice}, `xsstate` = #{xsstate}, `state` = #{state}, `classtype` = #{classtype}, `view_good_name` = #{viewGoodName}
and id = #{id} and produc_num = #{producNum}
