mapper.select:
select p.`id`,p.`produc_num`,p.`produc_code`,p.`produc_name`,p.`mfrsid`,p.`brandid`,p.`brandnum`,p.`factory`,p.`produc_factory`,p.`proid`,p.`parts_style`, p.`unitid`,p.`batchid`,p.`refractivityid`,p.`tax`,p.`tax_price`,p.`trade_price`,p.`transfer_price`,p.`retail_price`,p.`xsstate`,p.`state`, m.mfrsname,b.brandname,u.unitname,p.`classtype` from aa_parts p left join c_mfrs m on p.mfrsid=m.mfrsnum left join c_brand b on p.brandnum=b.brandnum AND p.`mfrsid`=b.`mfrsid` left join c_unit u on p.unitid= u.unitid
and p.produc_num LIKE concat(concat('%', #{producNum}),'%') and p.produc_name LIKE concat(concat('%', #{producName}),'%') and p.produc_code LIKE concat(concat('%', #{goodsCode}),'%') and m.mfrsname LIKE concat(concat('%', #{mfrsname}),'%') and b.brandname LIKE concat(concat('%', #{brandname}),'%')
and #{retailPrice} <= p.retail_price
and p.retail_price <= #{retailPrice2}
and #{taxPrice} <= p.tax_price
and p.retail_price <= #{taxPrice2}
and p.parts_style = #{partsStyle} and p.xsstate = #{xsstate} and p.state= 1
order by ${sort} ${order}
order by p.id desc
limit #{offset}, #{limit}
