mapper.select:
select
p.`id`,p.`produc_num`,p.`produc_code`,p.`produc_name`,p.`mfrsid`,p.`brandid`,p.`brandnum`,p.`factory`,p.`produc_factory`,p.`produc_factorycolor`,p.`produc_color`,
p.`unitid`,p.`batchid`,p.`size`,p.`technology_id`,p.`materialid`,p.`color`,p.`style_id`,p.`year`,p.`tax`,p.`tax_price`,p.`trade_price`,
p.`transfer_price`,p.`retail_price`,p.`xsstate`,p.`state`,
m.mfrsname,b.brandname,u.unitname,p.`classtype`,p.`view_good_name`
from aa_produca p left join c_mfrs m on p.mfrsid=m.mfrsnum left join c_brand b on p.brandnum=b.brandnum AND p.`mfrsid`=b.`mfrsid`
left join c_unit u on p.unitid= u.unitid

   and p.produc_num  LIKE concat(concat('%', #{producNum}),'%')
      and p.produc_code  LIKE concat(concat('%', #{goodsCode}),'%')


    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}
    and p.produc_name LIKE concat(concat('%', #{producName}),'%')


    and ABS(p.retail_price) >= ABS(#{retailPrice})
    and ABS(p.retail_price) <= ABS(#{retailPrice2})
    and ABS(p.tax_price) >= ABS(#{taxPrice})
    and ABS(p.tax_price) <= ABS(#{taxPrice2})


    and p.produc_factory = #{producFactory}
    and p.produc_color = #{producColor}
    and p.size = #{size}
    and p.technology_id = #{technologyId}
    and p.materialid = #{materialid}
    and p.xsstate = #{xsstate}
    and p.style_id = #{styleId}
    and p.view_good_name = #{viewGoodName}

    and p.produc_num LIKE concat(concat('%', #{num}),'%')
    and p.retail_price = #{oldMoney}
    and p.state=1 and m.xsstate=0 and m.state=1 and b.xsstate=0 and b.state=1



        order by ${sort} ${order}


          order by  p.id desc



    limit #{offset}, #{limit}

select count(*)   from aa_produca p left join c_mfrs m on p.mfrsid=m.mfrsnum left join c_brand b on p.brandnum=b.brandnum AND p.`mfrsid`=b.`mfrsid`
left join c_unit u on p.unitid= u.unitid

    and p.produc_num  LIKE concat(concat('%', #{producNum}),'%')
    and p.produc_code  LIKE concat(concat('%', #{goodsCode}),'%')

    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}
    and p.produc_name LIKE concat(concat('%', #{producName}),'%')


    and ABS(p.retail_price) >= ABS(#{retailPrice})
    and ABS(p.retail_price) <= ABS(#{retailPrice2})
    and ABS(p.tax_price) >= ABS(#{taxPrice})
    and ABS(p.tax_price) <= ABS(#{taxPrice2})


    and p.produc_factory = #{producFactory}
    and p.produc_color = #{producColor}
    and p.size = #{size}
    and p.technology_id = #{technologyId}
    and p.materialid = #{materialid}
    and p.xsstate = #{xsstate}
    and p.style_id = #{styleId}
    and p.view_good_name = #{viewGoodName}

    and p.produc_num LIKE concat(concat('%', #{num}),'%')
    and p.retail_price = #{oldMoney}
   and p.state=1 and m.xsstate=0 and m.state=1 and b.xsstate=0 and b.state=1

mapper.update:
update aa_produca
`produc_num` = #{producNum}, `produc_code` = #{producCode}, `produc_name` = #{producName}, `mfrsid` = #{mfrsid}, `brandid` = #{brandid}, `brandnum` = #{brandnum}, `factory` = #{factory}, `produc_factory` = #{producFactory}, `produc_factorycolor` = #{producFactorycolor}, `produc_color` = #{producColor}, `unitid` = #{unitid}, `batchid` = #{batchid}, `size` = #{size}, `technology_id` = #{technologyId}, `materialid` = #{materialid}, `color` = #{color}, `style_id` = #{styleId}, `year` = #{year}, `tax` = #{tax}, `tax_price` = #{taxPrice}, `trade_price` = #{tradePrice}, `transfer_price` = #{transferPrice}, `retail_price` = #{retailPrice}, `xsstate` = #{xsstate}, `state` = #{state}, `classtype` = #{classtype}, `view_good_name` = #{viewGoodName}
and id = #{id} and produc_num = #{producNum}

mapper.select:
select
  pr.`produc_factory`,pr.`produc_color`,ac.`materialname`,pr.`size`
from  aa_produca pr left join c_material ac on ac.materialid =
pr.materialid
where pr.produc_num = #{value}

		select  brandid,brandnum ,brandname  from c_brand

            and mfrsid = #{mfrsid}
            and goodsid = #{goodsid}
            and xsstate = #{xsstate}

		 group by brandid
