mapper.select:
select
p.`id`,p.`produc_num`,p.`produc_code`,p.`produc_name`,p.`mfrsid`,p.`brandid`,p.`factory`,p.`produc_factory`,p.`produc_factorycolor`,p.`produc_color`,
p.`unitid`,p.`batchid`,p.`size`,p.`technology_id`,p.`materialid`,p.`color`,p.`style_id`,p.`year`,p.`tax`,p.`tax_price`,p.`trade_price`,p.`transfer_price`,p.`retail_price`,
m.mfrsname,b.brandname,u.unitname
from aa_produca p left join c_mfrs m on p.mfrsid=m.mfrsid left join c_brand b on p.brandid=b.brandid left join
c_unit u on p.unitid= u.unitid

    and p.id = #{id}
    and p.produc_num = #{producNum}
    and p.produc_code = #{producCode}
    and p.produc_name = #{producName}
    and p.mfrsid = #{mfrsid}
    and p.brandid = #{brandid}
    and p.factory = #{factory}
    and p.produc_factory = #{producFactory}
    and p.produc_factorycolor =
        #{producFactorycolor}

    and p.produc_color = #{producColor}
    and p.unitid = #{unitid}
    and p.batchid = #{batchid}
    and p.size = #{size}
    and p.technology_id = #{technologyId}
    and p.materialid = #{materialid}
    and p.color = #{color}
    and p.style_id = #{styleId}
    and p.year = #{year}
    and p.tax = #{tax}
    and p.ax_price = #{taxPrice}
    and p.trade_price = #{tradePrice}
    and p.transfer_price = #{transferPrice}
    and p.retail_price = #{retailPrice}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}
    and u.unitname = #{unitname}



        order by ${sort} ${order}


        order by id



    limit #{offset}, #{limit}

select count(*) from aa_produca

    and id = #{id}
    and produc_num = #{producNum}
    and produc_code = #{producCode}
    and produc_name = #{producName}
    and mfrsid = #{mfrsid}
    and brandid = #{brandid}
    and factory = #{factory}
    and produc_factory = #{producFactory}
    and produc_factorycolor =
        #{producFactorycolor}

    and produc_color = #{producColor}
    and unitid = #{unitid}
    and batchid = #{batchid}
    and size = #{size}
    and technology_id = #{technologyId}
    and materialid = #{materialid}
    and color = #{color}
    and style_id = #{styleId}
    and year = #{year}
    and tax = #{tax}
    and tax_price = #{taxPrice}
    and trade_price = #{tradePrice}
    and transfer_price = #{transferPrice}
    and retail_price = #{retailPrice}
