mapper.select:
select
p.`id`,p.`produc_num`,p.`produc_code`,p.`produc_name`,p.`mfrsid`,p.`brandid`,p.`brandnum`,p.`factory`,p.`produc_factory`,p.`produc_factorycolor`,p.`produc_color`,
p.`unitid`,p.`batchid`,p.`size`,p.`technology_id`,p.`materialid`,p.`color`,p.`style_id`,p.`year`,p.`tax`,p.`tax_price`,p.`trade_price`,
p.`transfer_price`,p.`retail_price`,p.`status`,p.`state`,
m.mfrsname,b.brandname,u.unitname
from aa_produca p left join c_mfrs m on p.mfrsid=m.mfrsid left join c_brand b on p.brandnum=b.brandnum left join
c_unit u on p.unitid= u.unitid

    and p.produc_num = #{producNum}
    and p.produc_name = #{producName}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}

        and p.retail_price between #{retailPrice,jdbcType=VARCHAR} and #{retailPrice2,jdbcType=VARCHAR}


        and p.tax_price between #{taxPrice,jdbcType=VARCHAR} and #{taxPrice2,jdbcType=VARCHAR}



    and p.produc_factory = #{producFactory}
    and p.produc_color = #{producColor}
    and p.size = #{size}
    and p.technology_id = #{technologyId}
    and p.materialid = #{materialid}
    and p.status = #{status}
    and p.style_id = #{styleId}

and p.state= 1
group by p.id order by p.status asc ,p.id asc

select count(*) from aa_produca

    and id = #{id}
    and produc_num = #{producNum}
    and produc_code = #{producCode}
    and produc_name = #{producName}
    and mfrsid = #{mfrsid}
    and brandid = #{brandid}

    and brandnum = #{brandnum}
    and factory = #{factory}
    and produc_factory = #{producFactory}
    and produc_factorycolor =
        #{producFactorycolor}

    and produc_color = #{producColor}
    and unitid = #{unitid}
    and batchid = #{batchid}
    and size = #{size}
    and technology_id = #{technologyId}
    and materialid = #{materialid}
    and color = #{color}
    and style_id = #{styleId}
    and year = #{year}
    and tax = #{tax}
    and tax_price = #{taxPrice}
    and trade_price = #{tradePrice}
    and transfer_price = #{transferPrice}
    and retail_price = #{retailPrice}
    and status = #{status}
    and state = #{state}
