mapper.select:
select m.mfrsname,b.brandname,u.unitname,
s.`id`,s.`produc_num`,s.`produc_code`,s.`produc_name`,s.`mfrsid`,s.`brandid`,s.`brandnum`,s.`factory`,s.`produc_factory`,
s.`proid`,s.`unitid`,s.`batchid`,s.`leixing`,s.`tax`,s.`tax_price`,s.`trade_price`,s.`transfer_price`,s.`retail_price`,
s.`xsstate`,s.`state`
from aa_shiguang s left join c_mfrs m on s.mfrsid=m.mfrsid left join c_brand b on s.brandnum=b.brandnum left join
c_unit u on s.unitid= u.unitid

    and s.produc_num = #{producNum}
    and s.produc_name = #{producName}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}

        and   #{retailPrice} <=  s.retail_price and s.retail_price <=  #{retailPrice2}


        and   #{taxPrice} <=  s.tax_price and s.retail_price <=  #{taxPrice2}

    and s.xsstate = #{xsstate}
    and  s.state= 1

group by s.id order by s.xsstate asc ,s.id asc

select count(*) from aa_shiguang s left join c_mfrs m on s.mfrsid=m.mfrsid left join c_brand b on s.brandnum=b.brandnum left join
c_unit u on s.unitid= u.unitid

    and s.produc_num = #{producNum}
    and s.produc_name = #{producName}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}

        and   #{retailPrice} <=  s.retail_price and s.retail_price <=  #{retailPrice2}


        and   #{taxPrice} <=  s.tax_price and s.retail_price <=  #{taxPrice2}

    and s.xsstate = #{xsstate}
    and  s.state= 1

select count(*) from aa_shiguangs left join c_mfrs m on s.mfrsid=m.mfrsid left join c_brand b on s.brandnum=b.brandnum left join
c_unit u on s.unitid= u.unitid

    and s.produc_name = #{producName}
    and s.xsstate = #{xsstate}
    and s.state= 1
