mapper.select:
select
t.`id`,t.`produc_num`,t.`produc_code`,t.`produc_name`,t.`mfrsid`,t.`brandid`,t.`brandnum`,t.`factory`,t.`produc_factory`,t.`unitid`,t.`produc_factorycolor`,
t.`produc_color`,t.`batchid`,t.`size`,t.`function`,t.`color`,t.`style_id`,t.`year`,t.`tax`,t.`tax_price`,t.`trade_price`,
t.`transfer_price`,t.`retail_price`, m.`mfrsname`,b.`brandname`,u.`unitname`,t.`xsstate`,t.`state`,t.`classtype`
from aa_tyj t left join c_mfrs m on t.mfrsid=m.mfrsnum left join c_brand b on t.brandnum=b.brandnum AND t.`mfrsid`=b.`mfrsid`
left join c_unit u on t.unitid= u.unitid

    and t.produc_num LIKE concat(concat('%', #{producNum}),'%')
    and t.produc_name LIKE concat(concat('%', #{producName}),'%')
    and t.produc_code LIKE concat(concat('%', #{goodsCode}),'%')
    and m.mfrsname LIKE concat(concat('%', #{mfrsname}),'%')
    and b.brandname LIKE concat(concat('%', #{brandname}),'%')

        and   #{retailPrice} <=  t.retail_price


        and t.retail_price <=  #{retailPrice2}


        and   #{taxPrice} <=  t.tax_price


        and t.retail_price <=  #{taxPrice2}


    and t.produc_factory = #{producFactory}
    and t.produc_color = #{producColor}
    and t.size = #{size}
    and t.xsstate = #{xsstate}
    and t.style_id = #{styleId}
    and  t.state= 1




        order by ${sort} ${order}


          order by t.id desc



    limit #{offset}, #{limit}

select count(*)   from aa_tyj t left join c_mfrs m on t.mfrsid=m.mfrsnum left join c_brand b on t.brandnum=b.brandnum AND t.`mfrsid`=b.`mfrsid`
left join c_unit u on t.unitid= u.unitid

    and t.produc_num LIKE concat(concat('%', #{producNum}),'%')
    and t.produc_name LIKE concat(concat('%', #{producName}),'%')
    and t.produc_code LIKE concat(concat('%', #{goodsCode}),'%')
    and m.mfrsname LIKE concat(concat('%', #{mfrsname}),'%')
    and b.brandname LIKE concat(concat('%', #{brandname}),'%')

        and   #{retailPrice} <=  t.retail_price


        and t.retail_price <=  #{retailPrice2}


        and   #{taxPrice} <=  t.tax_price


        and t.retail_price <=  #{taxPrice2}


    and t.produc_factory = #{producFactory}
    and t.produc_color = #{producColor}
    and t.size = #{size}
    and t.xsstate = #{xsstate}
    and t.style_id = #{styleId}
    and  t.state= 1
