mapper.select:
select
t.`id`,t.`produc_num`,t.`produc_code`,t.`produc_name`,t.`mfrsid`,t.`brandid`,t.`factory`,t.`produc_factory`,t.`unitid`,t.`produc_factorycolor`,
t.`produc_color`,t.`batchid`,t.`size`,t.`function`,t.`color`,t.`style_id`,t.`year`,t.`tax`,t.`tax_price`,t.`trade_price`,
t.`transfer_price`,t.`retail_price`, m.`mfrsname`,b.`brandname`,u.`unitname`
from aa_tyj t left join c_mfrs m on t.mfrsid=m.mfrsid left join c_brand b on t.brandid=b.brandid
left join c_unit u on t.unitid= u.unitid

    and t.id = #{id}
    and t.produc_num = #{producNum}
    and t.produc_code = #{producCode}
    and t.produc_name = #{producName}
    and t.mfrsid = #{mfrsid}
    and t.brandid = #{brandid}
    and t.factory = #{factory}
    and t.produc_factory = #{producFactory}
    and t.unitid = #{unitid}
    and t.produc_factorycolor =
        #{producFactorycolor}

    and t.produc_color = #{producColor}
    and t.batchid = #{batchid}
    and t.size = #{size}
    and t.function = #{function}
    and t.color = #{color}
    and t.style_id = #{styleId}
    and t.year = #{year}
    and t.tax = #{tax}
    and t.tax_price = #{taxPrice}
    and t.trade_price = #{tradePrice}
    and t.transfer_price = #{transferPrice}
    and t.retail_price = #{retailPrice}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}
    and u.unitname = #{unitname}



        order by ${sort} ${order}


        order by id



    limit #{offset}, #{limit}

select count(*) from aa_tyj

    and id = #{id}
    and produc_num = #{producNum}
    and produc_code = #{producCode}
    and produc_name = #{producName}
    and mfrsid = #{mfrsid}
    and brandid = #{brandid}
    and factory = #{factory}
    and produc_factory = #{producFactory}
    and unitid = #{unitid}
    and produc_factorycolor =
        #{producFactorycolor}

    and produc_color = #{producColor}
    and batchid = #{batchid}
    and size = #{size}
    and function = #{function}
    and color = #{color}
    and style_id = #{styleId}
    and year = #{year}
    and tax = #{tax}
    and tax_price = #{taxPrice}
    and trade_price = #{tradePrice}
    and transfer_price = #{transferPrice}
    and retail_price = #{retailPrice}
