mapper.select:
select l.cyl,s.sph,
m.`mfrsname`,b.`brandname`,u.`unitname`,
y.`id`,y.`produc_num`,y.`produc_code`,y.`produc_name`,y.`mfrsid`,y.`brandid`,y.`brandnum`,y.`proid`,y.`unitid`,y.`sph_id`,y.`cyl_id`,y.`curvature`,y.`diameter`,y.`factory`,
y.`usage_id`,y.`type_id`,y.`use_days`,y.`upday`,y.`downday`,y.`color`,y.`invisible_id`,y.`caizhi_id`,y.`tax`,y.`tax_price`,y.`trade_price`,y.`transfer_price`,
y.`retail_price`,y.`status`,y.`state`
from aa_yxcp y left join c_mfrs m on y.mfrsid=m.mfrsid left join c_brand b on y.brandnum=b.brandnum
left join c_unit u on y.unitid= u.unitid left join a_cyl l on l.cyl_id=y.cyl_id left join a_sph s on
s.sph_id=y.sph_id

    and y.produc_num = #{producNum}
    and y.produc_name = #{producName}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}

        and y.retail_price between #{retailPrice,jdbcType=VARCHAR} and #{retailPrice2,jdbcType=VARCHAR}


        and y.tax_price between #{taxPrice,jdbcType=VARCHAR} and #{taxPrice2,jdbcType=VARCHAR}

    and y.usage_id = #{usageId}
    and y.type_id = #{typeId}
    and y.status = #{status}


        and s.sph between #{sph,jdbcType=VARCHAR} and (#{sph2,jdbcType=VARCHAR})


        and l.cyl between #{cyl,jdbcType=VARCHAR} and #{cyl2,jdbcType=VARCHAR}

    and y.state=1

group by y.id order by y.status asc ,y.id asc

select count(*) from aa_yxcp

    and id = #{id}
    and produc_num = #{producNum}
    and produc_code = #{producCode}
    and produc_name = #{producName}
    and mfrsid = #{mfrsid}
    and brandid = #{brandid}
    and brandnum = #{brandnum}
    and proid = #{proid}
    and unitid = #{unitid}
    and sph_id = #{sphId}
    and cyl_id = #{cylId}
    and curvature = #{curvature}
    and diameter = #{diameter}
    and factory = #{factory}
    and usage_id = #{usageId}
    and type_id = #{typeId}
    and use_days = #{useDays}
    and upday = #{upday}
    and downday = #{downday}
    and color = #{color}
    and invisible_id = #{invisibleId}
    and caizhi_id = #{caizhiId}
    and tax = #{tax}
    and tax_price = #{taxPrice}
    and trade_price = #{tradePrice}
    and transfer_price = #{transferPrice}
    and retail_price = #{retailPrice}
    and status = #{status}
    and state = #{state}
