mapper.select:
select l.cyl,s.sph,
m.`mfrsname`,b.`brandname`,u.`unitname`,
y.`id`,y.`produc_num`,y.`produc_code`,y.`produc_name`,y.`mfrsid`,y.`brandid`,y.`brandnum`,y.`proid`,y.`unitid`,y.`sph_id`,y.`cyl_id`,y.`curvature`,y.`diameter`,y.`factory`,
y.`usage_id`,y.`type_id`,y.`use_days`,y.`upday`,y.`downday`,y.`color`,y.`invisible_id`,y.`caizhi_id`,y.`tax`,y.`tax_price`,y.`trade_price`,y.`transfer_price`,
y.`retail_price`,y.`xsstate`,y.`state`,y.`classtype`
from aa_yxcp y left join c_mfrs m on y.mfrsid=m.mfrsnum left join c_brand b on y.brandnum=b.brandnum AND y.`mfrsid`=b.`mfrsid`
left join c_unit u on y.unitid= u.unitid left join a_cyl l on l.cyl_id=y.cyl_id left join a_sph s on
s.sph_id=y.sph_id

    and y.produc_num LIKE concat(concat('%', #{producNum}),'%')
    and y.produc_name LIKE concat(concat('%', #{producName}),'%')
    and y.produc_code LIKE concat(concat('%', #{goodsCode}),'%')
    and m.mfrsname LIKE concat(concat('%', #{mfrsname}),'%')
    and b.brandname LIKE concat(concat('%', #{brandname}),'%')

        and   #{retailPrice} <=  y.retail_price


        and y.retail_price <=  #{retailPrice2}


        and   #{taxPrice} <=  y.tax_price


        and y.retail_price <=  #{taxPrice2}

    and y.usage_id = #{usageId}
    and y.type_id = #{typeId}
    and y.xsstate = #{xsstate}
    and y.classtype = #{classtype}


        and s.sph between #{sph,jdbcType=VARCHAR} and (#{sph2,jdbcType=VARCHAR})


        and l.cyl between #{cyl,jdbcType=VARCHAR} and #{cyl2,jdbcType=VARCHAR}

    and y.state=1




        order by ${sort} ${order}


          order by  y.id desc



    limit #{offset}, #{limit}

select count(*)  from aa_yxcp y left join c_mfrs m on y.mfrsid=m.mfrsnum left join c_brand b on y.brandnum=b.brandnum AND y.`mfrsid`=b.`mfrsid`
left join c_unit u on y.unitid= u.unitid left join a_cyl l on l.cyl_id=y.cyl_id left join a_sph s on
s.sph_id=y.sph_id

    and y.produc_num LIKE concat(concat('%', #{producNum}),'%')
    and y.produc_name LIKE concat(concat('%', #{producName}),'%')
    and y.produc_code LIKE concat(concat('%', #{goodsCode}),'%')
    and m.mfrsname LIKE concat(concat('%', #{mfrsname}),'%')
    and b.brandname LIKE concat(concat('%', #{brandname}),'%')

        and   #{retailPrice} <=  y.retail_price


        and y.retail_price <=  #{retailPrice2}


        and   #{taxPrice} <=  y.tax_price


        and y.retail_price <=  #{taxPrice2}

    and y.usage_id = #{usageId}
    and y.type_id = #{typeId}
    and y.xsstate = #{xsstate}
    and y.classtype = #{classtype}


        and s.sph between #{sph,jdbcType=VARCHAR} and (#{sph2,jdbcType=VARCHAR})


        and l.cyl between #{cyl,jdbcType=VARCHAR} and #{cyl2,jdbcType=VARCHAR}

    and y.state=1
