mapper.select:
select
m.`mfrsname`,b.`brandname`,u.`unitname`,
y.`id`,y.`produc_num`,y.`produc_code`,y.`produc_name`,y.`mfrsid`,y.`brandid`,y.`proid`,y.`unitid`,y.`sph_id`,y.`cyl_id`,y.`curvature`,y.`diameter`,y.`factory`,
y.`usage_id`,y.`type_id`,y.`use_days`,y.`upday`,y.`downday`,y.`color`,y.`invisible_id`,y.`caizhi_id`,y.`tax`,y.`tax_price`,y.`trade_price`,y.`transfer_price`,
y.`retail_price`
from aa_yxcp y left join c_mfrs m on y.mfrsid=m.mfrsid left join c_brand b on y.brandid=b.brandid
left join c_unit u on y.unitid= u.unitid

    and y.id = #{id}
    and y.produc_num = #{producNum}
    and y.produc_code = #{producCode}
    and y.produc_name = #{producName}
    and y.mfrsid = #{mfrsid}
    and y.brandid = #{brandid}
    and y.proid = #{proid}
    and y.unitid = #{unitid}
    and y.sph_id = #{sphId}
    and y.cyl_id = #{cylId}
    and y.curvature = #{curvature}
    and y.diameter = #{diameter}
    and y.factory = #{factory}
    and y.usage_id = #{usageId}
    and y.type_id = #{typeId}
    and y.use_days = #{useDays}
    and y.upday = #{upday}
    and y.downday = #{downday}
    and y.color = #{color}
    and y.invisible_id = #{invisibleId}
    and y.caizhi_id = #{caizhiId}
    and y.tax = #{tax}
    and y.tax_price = #{taxPrice}
    and y.trade_price = #{tradePrice}
    and y.transfer_price = #{transferPrice}
    and y.retail_price = #{retailPrice}
    and m.mfrsname = #{mfrsname}
    and b.brandname = #{brandname}
    and u.unitname = #{unitname}



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

select count(*) from aa_yxcp

    and id = #{id}
    and produc_num = #{producNum}
    and produc_code = #{producCode}
    and produc_name = #{producName}
    and mfrsid = #{mfrsid}
    and brandid = #{brandid}
    and proid = #{proid}
    and unitid = #{unitid}
    and sph_id = #{sphId}
    and cyl_id = #{cylId}
    and curvature = #{curvature}
    and diameter = #{diameter}
    and factory = #{factory}
    and usage_id = #{usageId}
    and type_id = #{typeId}
    and use_days = #{useDays}
    and upday = #{upday}
    and downday = #{downday}
    and color = #{color}
    and invisible_id = #{invisibleId}
    and caizhi_id = #{caizhiId}
    and tax = #{tax}
    and tax_price = #{taxPrice}
    and trade_price = #{tradePrice}
    and transfer_price = #{transferPrice}
    and retail_price = #{retailPrice}
