mapper.select:
select
`id`,`produc_num`,`produc_code`,`produc_name`,`mfrsid`,`brandid`,`factory`,`produc_factory`,`proid`,`unitid`,`sph_id`,`span_id`,`cyl_id`,`sph_up`,`sph_down`,`sph_span`,`cyl_up`,`cyl_down`,`cyl_span`,`curvature_one`,`curvature_yi`,`curvature_two`,`curvature_er`,`diameter_one`,`diameter_yi`,`contact_lens`,`cycle`,`usage_id`,`type_id`,`use_days`,`upday`,`downday`,`color`,`invisible_id`,`caizhi_id`,`combined_photometry`,`tax`,`tax_price`,`trade_price`,`transfer_price`,`retail_price`
from aa_yxdz

    and id = #{id}
    and produc_num = #{producNum}
    and produc_code = #{producCode}
    and produc_name = #{producName}
    and mfrsid = #{mfrsid}
    and brandid = #{brandid}
    and factory = #{factory}
    and produc_factory = #{producFactory}
    and proid = #{proid}
    and unitid = #{unitid}
    and sph_id = #{sphId}
    and span_id = #{spanId}
    and cyl_id = #{cylId}
    and sph_up = #{sphUp}
    and sph_down = #{sphDown}
    and sph_span = #{sphSpan}
    and cyl_up = #{cylUp}
    and cyl_down = #{cylDown}
    and cyl_span = #{cylSpan}
    and curvature_one = #{curvatureOne}
    and curvature_yi = #{curvatureYi}
    and curvature_two = #{curvatureTwo}
    and curvature_er = #{curvatureEr}
    and diameter_one = #{diameterOne}
    and diameter_yi = #{diameterYi}
    and contact_lens = #{contactLens}
    and cycle = #{cycle}
    and usage_id = #{usageId}
    and type_id = #{typeId}
    and use_days = #{useDays}
    and upday = #{upday}
    and downday = #{downday}
    and color = #{color}
    and invisible_id = #{invisibleId}
    and caizhi_id = #{caizhiId}
    and combined_photometry =
        #{combinedPhotometry}

    and tax = #{tax}
    and tax_price = #{taxPrice}
    and trade_price = #{tradePrice}
    and transfer_price = #{transferPrice}
    and retail_price = #{retailPrice}



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

