select
y.`id`,y.`produc_num`,y.`produc_code`,y.`produc_name`,y.`mfrsid`,y.`brandid`,y.`factory`,y.`produc_factory`,
y.`proid`,y.`unitid`,y.`sph_id`,y.`span_id`,y.`cyl_id`,y.`sph_up`,y.`sph_down`,y.`sph_span`,y.`cyl_up`,
y.`cyl_down`,y.`cyl_span`,y.`curvature_one`,y.`curvature_yi`,y.`curvature_two`,y.`curvature_er`,
y.`diameter_one`,y.`diameter_yi`,y.`contact_lens`,y.`cycle`,y.`usage_id`,u.`usage_name`,y.`type_id`,t.`type_name`,
y.`use_days`,y.`upday`,y.`downday`,y.`color`,y.`invisible_id`,y.`caizhi_id`,y.`combined_photometry`,
y.`tax`,y.`tax_price`,y.`trade_price`,y.`transfer_price`,y.`retail_price`
from aa_yxdz y left join p_type t on t.type_id = y.type_id
left join p_usage u on u.usage_id = y.usage_id

    and y.id = #{id}
    and y.produc_num = #{producNum}
    and y.produc_code = #{producCode}
    and y.produc_name = #{producName}
    and y.mfrsid = #{mfrsid}
    and y.brandid = #{brandid}
    and y.factory = #{factory}
    and y.produc_factory = #{producFactory}
    and y.proid = #{proid}
    and y.unitid = #{unitid}
    and y.sph_id = #{sphId}
    and y.span_id = #{spanId}
    and y.cyl_id = #{cylId}
    and y.sph_up = #{sphUp}
    and y.sph_down = #{sphDown}
    and y.sph_span = #{sphSpan}
    and y.cyl_up = #{cylUp}
    and y.cyl_down = #{cylDown}
    and y.cyl_span = #{cylSpan}
    and y.curvature_one = #{curvatureOne}
    and y.curvature_yi = #{curvatureYi}
    and y.curvature_two = #{curvatureTwo}
    and y.curvature_er = #{curvatureEr}
    and y.diameter_one = #{diameterOne}
    and y.diameter_yi = #{diameterYi}
    and y.contact_lens = #{contactLens}
    and y.cycle = #{cycle}
    and y.usage_id = #{usageId}
    and y.type_id = #{typeId}
    and y.use_days = #{useDays}
    and y.upday = #{upday}
    and y.downday = #{downday}
    and y.color = #{color}
    and y.invisible_id = #{invisibleId}
    and y.caizhi_id = #{caizhiId}
    and y.combined_photometry =
        #{combinedPhotometry}

    and y.tax = #{tax}
    and y.tax_price = #{taxPrice}
    and y.trade_price = #{tradePrice}
    and y.transfer_price = #{transferPrice}
    and y.retail_price = #{retailPrice}



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

