mapper.select:
select s.`id`,s.`cost_id`,s.`sale_number`,s.`member_number`,s.`sale_name`,s.`actual_money`,s.`payment_money`,s.`change_money`,s.`pay_model`, s.`model_money`,s.`pay_money`,s.`pay_way`,s.`front_money`,s.`settle_date`,s.`front_time`,d.`drawback_money` as drawBackMoney,d.`drackback_way` as drawBackWay,dt.`depart_name` as departmentName from settlement s join member m on m.card_number = s.member_number join department dt on dt.company_id = m.company_id left join drawback d on d.sale_number = s.sale_number
and m.company_id in (#{companyid}) and date_format(s.settle_date ,'%Y-%m-%d' ) >= (#{settleDateStart}) and date_format(s.settle_date ,'%Y-%m-%d' ) <= (#{settleDateEnd}) and m.state = #{state} and dt.depart_number = #{departNumber} and dt.depart_type = #{departType}
GROUP BY s.sale_number order by dt.depart_number

select count (*)
from settlement s join member m on m.card_number = s.member_number join department dt on dt.company_id = m.company_id  left join drawback d on d.sale_number = s.sale_number join sys_user su on su.username = s.sale_acount

	and m.company_id in (#{companyid})
	and date_format(s.settle_date ,'%Y-%m-%d' ) >= (#{settleDateStart})
	and date_format(s.settle_date ,'%Y-%m-%d' ) <= (#{settleDateEnd})
	and m.state = #{state}
	and su.store_num = #{departNumber}
	and dt.depart_type = #{departType}

select s.`sale_account`,s.`sale_name` from store_sales s join settlement st on st.sale_number = s.sale_number join member m on m.card_number = s.member_number join department d on d.company_id = m.company_id
and date_format(st.settle_date ,'%Y-%m-%d' ) >= (#{settleDateStart}) and date_format(st.settle_date ,'%Y-%m-%d' ) <= (#{settleDateEnd}) and m.state = #{state} and m.company_id in (#{companyid}) and s.store_num= #{departNumber}
GROUP BY s.sale_name

