mapper.select:
select s.`id`,s.`cost_id`,s.`sale_number`,s.`member_number`,s.`sale_name`,s.`actual_money`,s.`payment_money`,s.`change_money`,s.`pay_model`, s.`model_money`,s.`pay_money`,s.`pay_way`,s.`front_money`,s.`settle_date`,s.`front_time`,d.`drawback_money` as drawBackMoney,d.`drackback_way` as drawBackWay,dt.`depart_name` as departmentName from settlement s join member m on m.card_number = s.member_number join department dt on dt.company_id = m.company_id left join drawback d on d.sale_number = s.sale_number
and m.company_id in (#{companyid}) and date_format(s.settle_date ,'%Y-%m-%d' ) >= (#{settleDateStart}) and date_format(s.settle_date ,'%Y-%m-%d' ) <= (#{settleDateEnd}) or date_format(d.create_time ,'%Y-%m-%d' ) >= (#{settleDateStart}) and date_format(d.create_time ,'%Y-%m-%d' ) <= (#{settleDateEnd}) and m.state = #{state} and dt.depart_number = #{departNumber} and dt.depart_type = #{departType}
GROUP BY s.sale_number order by dt.depart_number

select count (*)
from settlement s join member m on m.card_number = s.member_number join department dt on dt.company_id = m.company_id  left join drawback d on d.sale_number = s.sale_number join sys_user su on su.username = s.sale_acount

	and m.company_id in (#{companyid})
	and date_format(s.settle_date ,'%Y-%m-%d' ) >= (#{settleDateStart})
	and date_format(s.settle_date ,'%Y-%m-%d' ) <= (#{settleDateEnd})
	and m.state = #{state}
	and su.store_num = #{departNumber}
	and dt.depart_type = #{departType}

select s.`sale_account`,s.`sale_name` from store_sales s join settlement st on st.sale_number = s.sale_number join member m on m.card_number = s.member_number join department d on d.company_id = m.company_id
and date_format(st.settle_date ,'%Y-%m-%d' ) >= (#{settleDateStart}) and date_format(st.settle_date ,'%Y-%m-%d' ) <= (#{settleDateEnd}) and m.state = #{state} and m.company_id in (#{companyid}) and s.store_num= #{departNumber}
GROUP BY s.sale_name

SELECT COUNT( ptometry_number ) AS ygcount FROM ( SELECT ptometry_number, company_id, create_time, username, role_id, store FROM ( SELECT kjyy.ptometry_number, u.company_id, kjyy.create_time, u.username, ur.role_id, u.store FROM jc_kjyy kjyy JOIN sys_user u ON kjyy.kjyy_optometry_name = u. NAME JOIN sys_user_role ur ON u.user_id = ur.user_id UNION ALL SELECT kjjy.ptometry_number, u.company_id, kjjy.create_time, u.username, ur.role_id, u.store FROM jc_kjjy kjjy JOIN sys_user u ON kjjy.kjjy_optometry_name = u. NAME JOIN sys_user_role ur ON u.user_id = ur.user_id UNION ALL SELECT sgjj.ptometry_number, u.company_id, sgjj.create_time, u.username, ur.role_id, u.store FROM jc_sgjj sgjj JOIN sys_user u ON sgjj.sgjj_optometry_name = u. NAME JOIN sys_user_role ur ON u.user_id = ur.user_id UNION ALL SELECT rx.ptometry_number, u.company_id, rx.create_time, u.username, ur.role_id, u.store FROM jc_rxjmjcj rx JOIN sys_user u ON rx.rx_optometry_name = u. NAME JOIN sys_user_role ur ON u.user_id = ur.user_id UNION ALL SELECT zy.ptometry_number, u.company_id, zy.create_time, u.username, ur.role_id, u.store FROM jc_zy zy JOIN sys_user u ON zy.zy_optometry_name = u. NAME JOIN sys_user_role ur ON u.user_id = ur.user_id UNION ALL SELECT sjxl.ptometry_number, u.company_id, sjxl.create_time, u.username, ur.role_id, u.store FROM jc_sjxl sjxl JOIN sys_user u ON sjxl.sjxl_optometry_name = u. NAME JOIN sys_user_role ur ON u.user_id = ur.user_id UNION ALL SELECT vst.ptometry_number, u.company_id, vst.create_time, u.username, ur.role_id, u.store FROM jc_vst vst JOIN sys_user u ON vst.vst_optometry_name = u. NAME JOIN sys_user_role ur ON u.user_id = ur.user_id UNION ALL SELECT crt.ptometry_number, u.company_id, crt.create_time, u.username, ur.role_id, u.store FROM jc_crt crt JOIN sys_user u ON crt.crt_optometry_name = u. NAME JOIN sys_user_role ur ON u.user_id = ur.user_id UNION ALL SELECT rgp.ptometry_number, u.company_id, rgp.create_time, u.username, ur.role_id, u.store FROM jc_rgp rgp JOIN sys_user u ON rgp.rgp_optometry_name = u. NAME JOIN sys_user_role ur ON u.user_id = ur.user_id UNION ALL SELECT yp.ptometry_number, u.company_id, yp.create_time, u.username, ur.role_id, u.store FROM jc_yaopin yp JOIN sys_user u ON yp.yp_optometry_name = u. NAME JOIN sys_user_role ur ON u.user_id = ur.user_id ) aia
and company_id in (#{companyId}) and date_format( create_time ,'%Y-%m-%d' ) >= (#{settleDateStart}) and date_format( create_time ,'%Y-%m-%d' ) <= (#{settleDateEnd}) and store = #{department} and username = #{username} and ( role_id = 7)
) ai

