mapper.select:
select `id`,`number`,`day`,`people`,`remarks`,`goodstype`,`classtypes`,`num`,`name`,`goods_num`,`goods_name`,`mfrsnum`,`mfrsname`,`brandnum`,`brandname`,`factory`,`old_price`,`new_price`,`flag` from all_retailprice

	 and id = #{id} 
	 and number = #{number} 
	 and day = #{day} 
	 and people = #{people} 
	 and remarks = #{remarks} 
	 and goodstype = #{goodstype} 
	 and classtypes = #{classtypes} 
	 and num = #{num} 
	 and name = #{name} 
	 and goods_num = #{goodsNum} 
	 and goods_name = #{goodsName} 
	 and mfrsnum = #{mfrsnum} 
	 and mfrsname = #{mfrsname} 
	 and brandnum = #{brandnum} 
	 and brandname = #{brandname} 
	 and factory = #{factory} 
	 and old_price = #{oldPrice} 
	 and new_price = #{newPrice} 
	 and flag = #{flag} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(*) from all_retailprice

	 and id = #{id} 
	 and number = #{number} 
	 and day = #{day} 
	 and people = #{people} 
	 and remarks = #{remarks} 
	 and goodstype = #{goodstype} 
	 and classtypes = #{classtypes} 
	 and num = #{num} 
	 and name = #{name} 
	 and goods_num = #{goodsNum} 
	 and goods_name = #{goodsName} 
	 and mfrsnum = #{mfrsnum} 
	 and mfrsname = #{mfrsname} 
	 and brandnum = #{brandnum} 
	 and brandname = #{brandname} 
	 and factory = #{factory} 
	 and old_price = #{oldPrice} 
	 and new_price = #{newPrice} 
	 and flag = #{flag}

select m.mfrsname,b.brandname,b.goodsid,a.mfrsid,a.brandnum,a.retail_price,a.classtype from c_brand b
join   aa_produca a on  a.brandnum=b.brandnum   AND a.mfrsid=b.mfrsid
join c_mfrs m on  m.mfrsnum=b.mfrsid

	and m.mfrsname = #{mfrsname}
	and b.brandname = #{brandname}
	and a.state=1 and m.xsstate=0 and m.state=1 and b.xsstate=0 and b.state=1



		order by ${sort} ${order}


		GROUP BY  b.brandnum  ,  b.mfrsid,a.retail_price
		order by b.brandid desc,a.id



	limit #{offset}, #{limit}

select m.mfrsname,b.brandname,b.goodsid,a.mfrsid,a.brandnum,a.retail_price,a.classtype from c_brand b
join   aa_parts a on  a.brandnum=b.brandnum   AND a.mfrsid=b.mfrsid
join c_mfrs m on  m.mfrsnum=b.mfrsid

	and m.mfrsname = #{mfrsname}
	and b.brandname = #{brandname}
	and a.state=1 and m.xsstate=0 and m.state=1 and b.xsstate=0 and b.state=1



		order by ${sort} ${order}


		GROUP BY  b.brandnum  ,  b.mfrsid,a.retail_price
		order by b.brandid desc,a.id



	limit #{offset}, #{limit}

