mapper.select:
		select `id`,`number`,`day`,`people`,`remarks`,`num`,`name`,`mfrsnum`,`mfrsname`,`brandnum`,`brandname`,`factory`,`old_price`,`new_price` from all_retailprice

		  		   and id = #{id} 
		  		   and number = #{number} 
		  		   and day = #{day} 
		  		   and people = #{people} 
		  		   and remarks = #{remarks} 
		  		   and num = #{num} 
		  		   and name = #{name} 
		  		   and mfrsnum = #{mfrsnum} 
		  		   and mfrsname = #{mfrsname} 
		  		   and brandnum = #{brandnum} 
		  		   and brandname = #{brandname} 
		  		   and factory = #{factory} 
		  		   and old_price = #{oldPrice} 
		  		   and new_price = #{newPrice} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from all_retailprice

  		   and id = #{id} 
  		   and number = #{number} 
  		   and day = #{day} 
  		   and people = #{people} 
  		   and remarks = #{remarks} 
  		   and num = #{num} 
  		   and name = #{name} 
  		   and mfrsnum = #{mfrsnum} 
  		   and mfrsname = #{mfrsname} 
  		   and brandnum = #{brandnum} 
  		   and brandname = #{brandname} 
  		   and factory = #{factory} 
  		   and old_price = #{oldPrice} 
  		   and new_price = #{newPrice}

select m.mfrsname,b.brandname,b.goodsid,a.mfrsid,a.brandnum,a.retail_price from c_brand b
join   aa_parts a on  a.brandnum=b.brandnum   AND a.`mfrsid`=b.`mfrsid`
join c_mfrs m on  m.mfrsnum=b.mfrsid

	and b.brandid = #{brandid}



		order by ${sort} ${order}


		GROUP BY  b.brandnum  ,  b.`mfrsid`,a.retail_price
		order by b.brandid desc



	limit #{offset}, #{limit}
