mapper.select:
		select `id`,`cost_id`,`member_number`,`actual_money`,`payment_money`,`change_money`,`pay_model`,`model_money`,`pay_money` from settlement
          
		  		   and id = #{id} 
			       and sale_number like concat('%', #{saleNumber},'%') 
		  		   and member_number like concat('%', #{memberNumber},'%') 
		  		   and actual_money = #{actualMoney} 
		  		   and payment_money = #{paymentMoney} 
		  		   and change_money = #{changeMoney} 
		  		   and pay_model = #{payModel} 
		  		   and pay_money = #{payMoney} 
		  		
        
            
                order by ${sort} ${order}
            
			
                order by id desc
			
        
		
			limit #{offset}, #{limit}

select count(*) from settlement
   
  		   and id = #{id} 
	       and sale_number like concat('%', #{saleNumber},'%') 
	       and member_number like concat('%', #{memberNumber},'%') 
  		   and actual_money = #{actualMoney} 
  		   and payment_money = #{paymentMoney} 
  		   and change_money = #{changeMoney} 
  		   and pay_model = #{payModel} 
  		   and pay_money = #{payMoney}

select sl.`id`,sl.`sale_number`,sl.`member_number`,m.`name` as memberName,sl.`actual_money`,sl.`model_money`,sl.`pay_money`,sl.`pay_way`,sl.`front_money`,sl.`settle_date`,c.`original_price` from settlement sl
join cost c on c.id = sl.cost_id
join member m on sl.member_number = m.card_number
 and m.depart_number = #{departNumber} 
and FIND_IN_SET(m.company_id,#{companyid})
 and m.card_number like concat('%', #{cardNumber},'%') 
 and sl.sale_number like concat('%', #{saleNumber},'%') 
 and sl.pay_way = #{payWay}
 and m.state = #{state} 

	
		order by ${sort} ${order}
	
	
		order by sl.`settle_date` desc
	


	limit #{offset}, #{limit}

select count(*) from settlement sl
join member m on sl.member_number = m.card_number
 and m.depart_number = #{departNumber} 
and FIND_IN_SET(m.company_id,#{companyid})
 and m.card_number like concat('%', #{cardNumber},'%') 
 and sl.sale_number like concat('%', #{saleNumber},'%') 
 and sl.pay_way = #{payWay}
 and m.state = #{state}

select sl.`id`,sl.`sale_number`,sl.`member_number`,m.`name` as memberName,sl.`actual_money`,sl.`model_money`,sl.`pay_money`,sl.`pay_way`,sl.`front_money`,sl.`settle_date` from settlement sl
join member m on sl.member_number = m.card_number
 where sl.sale_number = #{value}

select sl.`id`,sl.`sale_number`,sl.`member_number`,m.`name` as memberName,m.`sex` as sex,m.`age` as age,sl.`actual_money`,sl.`pay_money`,sl.`model_money`,sl.`pay_way`,
sl.`front_money`,sl.`settle_date`,c.`original_price`
 from settlement sl
 join member m on sl.member_number = m.card_number
 join cost c on c.id = sl.cost_id
and FIND_IN_SET(m.company_id,#{companyid})
 and m.card_number like concat('%', #{cardNumber},'%') 
 and m.name like concat('%', #{name},'%') 
 and m.phone_1 = #{phone1} 
 and sl.sale_number like concat('%', #{saleNumber},'%') 
 and c.type != #{type}
 and m.state = #{state} 

	
		order by ${sort} ${order}
	
	
		order by sl.`settle_date` desc
	


	limit #{offset}, #{limit}

select count(*)
from settlement sl
join member m on sl.member_number = m.card_number
join cost c on c.id = sl.cost_id
and FIND_IN_SET(m.company_id,#{companyid})
 and m.card_number like concat('%', #{cardNumber},'%') 
 and sl.sale_number like concat('%', #{saleNumber},'%') 
 and sl.pay_way = #{payWay}
 and c.type != #{type}
 and m.state = #{state}
