mapper.select:
select
s.`id`,s.`goods_num`,s.`goods_code`,s.`goods_name`,s.`goods_count`,s.`goods_type`,g.`goodsname` as gdname, s.`mfrsid`,s.`brandname`,s.`retail_price`,
s.`price_sum`, s.`cost_price`, s.`cost_sum`, s.`whole_price`, s.`whole_sum`,s.`position_id`, po.`position_name`, s.`create_time`,
s.`danju_number`, s.`order_number`, s.`yundan_number`, s.`zhidan_people`, s.`danju_day`, s.`tuihuo_number`,
s.`factory_number`, s.`beizhu`, s.`unit`,m.mfrsname,s.`batch`,s.`zhuce_number`,s.`produce_day`,s.`useday`,s.`classtype`,
s.`factory`,s.`status`,s.`username`,s.`returnzt`,s.`stockorder`
from stock_order s
join c_mfrs m on s.mfrsid=m.mfrsnum
join c_position po on s.position_id=po.position_id
join c_goods g on s.goods_type=g.goodsid

    and s.id = #{id}
    and s.goods_num = #{goodsNum}
    and s.goods_code = #{goodsCode}
    and s.goods_name = #{goodsName}
    and s.goods_count = #{goodsCount}
    and s.goods_type = #{goodsType}
    and s.mfrsid = #{mfrsid}
    and m.mfrsname  LIKE concat(concat('%', #{mfrsname}),'%')
    and s.brandname = #{brandname}
    and s.retail_price = #{retailPrice}
    and s.price_sum = #{priceSum}
    and s.cost_price = #{costPrice}
    and s.cost_sum = #{costSum}
    and s.whole_price = #{wholePrice}
    and s.whole_sum = #{wholeSum}
    and s.position_id = #{positionId}
    and s.position_name = #{positionName}
    and s.create_time = #{createTime}


    and s.danju_number LIKE concat(concat('%', #{danjuNumber}),'%')

    and s.order_number = #{orderNumber}
    and s.yundan_number = #{yundanNumber}
    and s.zhidan_people = #{zhidanPeople}
    and s.danju_day = #{danjuDay}
    and s.tuihuo_number = #{tuihuoNumber}
    and s.factory_number = #{factoryNumber}
    and s.beizhu = #{beizhu}
    and s.unit = #{unit}
     and s.batch = #{batch} 
     and s.zhuce_number = #{zhuceNumber} 
     and s.produce_day = #{produceDay} 
     and s.useday = #{useday} 
    and po.depart_number = #{departNumber}
    and FIND_IN_SET(po.company_id,#{companyId})
    and s.classtype = #{classtype} 
    and s.factory = #{factory} 
    and s.status = #{status} 
    and s.username = #{username} 
    and s.returnzt = #{returnzt} 
    and s.stockorder = #{stockorder} 
    and g.goodsid = #{goodsid} 

     and date_format(s.danju_day ,'%Y-%m-%d' ) >= (#{day1})
     and date_format(s.danju_day ,'%Y-%m-%d' ) <= (#{day2})




        order by ${sort} ${order}


        group by danju_number
        order by id desc



    limit #{offset}, #{limit}

SELECT COUNT(DISTINCT danju_number)
 from stock_order s
 join c_mfrs m on s.mfrsid=m.mfrsnum
 join c_position po on s.position_id=po.position_id
 join department d on po.depart_number= d.depart_number
 join c_goods g on s.goods_type=g.goodsid

     and s.id = #{id}
     and s.goods_num = #{goodsNum}
     and s.goods_code = #{goodsCode}
     and s.goods_name = #{goodsName}
     and s.goods_count = #{goodsCount}
     and s.goods_type = #{goodsType}
     and s.mfrsid = #{mfrsid}
     and m.mfrsname  LIKE concat(concat('%', #{mfrsname}),'%')
     and s.brandname = #{brandname}
     and s.retail_price = #{retailPrice}
     and s.price_sum = #{priceSum}
     and s.cost_price = #{costPrice}
     and s.cost_sum = #{costSum}
     and s.whole_price = #{wholePrice}
     and s.whole_sum = #{wholeSum}
     and s.position_id = #{positionId}
     and s.position_name = #{positionName}
     and s.create_time = #{createTime}


     and s.danju_number LIKE concat(concat('%', #{danjuNumber}),'%')

     and s.order_number = #{orderNumber}
     and s.yundan_number = #{yundanNumber}
     and s.zhidan_people = #{zhidanPeople}
     and s.danju_day = #{danjuDay}
     and s.tuihuo_number = #{tuihuoNumber}
     and s.factory_number = #{factoryNumber}
     and s.beizhu = #{beizhu}
     and s.unit = #{unit}
      and s.batch = #{batch} 
      and s.zhuce_number = #{zhuceNumber} 
      and s.produce_day = #{produceDay} 
      and s.useday = #{useday} 
     and po.depart_number = #{departNumber}
     and FIND_IN_SET(po.company_id,#{companyId})
     and s.classtype = #{classtype} 
     and s.factory = #{factory} 
     and s.status = #{status} 
     and s.username = #{username} 
     and s.returnzt = #{returnzt} 
     and s.stockorder = #{stockorder} 
     and g.goodsid = #{goodsid} 

      and date_format(s.danju_day ,'%Y-%m-%d' ) >= (#{day1})
      and date_format(s.danju_day ,'%Y-%m-%d' ) <= (#{day2})

mapper.update:
update stock_order
`goods_num` = #{goodsNum}, `goods_code` = #{goodsCode}, `goods_name` = #{goodsName}, `goods_count` = #{goodsCount}, `goods_type` = #{goodsType}, `mfrsid` = #{mfrsid}, `brandname` = #{brandname}, `retail_price` = #{retailPrice}, `price_sum` = #{priceSum}, `cost_price` = #{costPrice}, `cost_sum` = #{costSum}, `whole_price` = #{wholePrice}, `whole_sum` = #{wholeSum}, `position_id` = #{positionId}, `position_name` = #{positionName}, `create_time` = #{createTime}, `danju_number` = #{danjuNumber}, `order_number` = #{orderNumber}, `yundan_number` = #{yundanNumber}, `zhidan_people` = #{zhidanPeople}, `danju_day` = #{danjuDay}, `tuihuo_number` = #{tuihuoNumber}, `factory_number` = #{factoryNumber}, `beizhu` = #{beizhu}, `unit` = #{unit}, `batch` = #{batch}, `zhuce_number` = #{zhuceNumber}, `produce_day` = #{produceDay}, `useday` = #{useday} , `classtype` = #{classtype}, `factory` = #{factory} , `status` = #{status} , `username` = #{username}, `returnzt` = #{returnzt}, `stockorder` = #{stockorder}
and danju_number = #{danjuNumber} and goods_num = #{goodsNum} and goods_name = #{goodsName}

mapper.select:
		select o.goods_name,o.goods_code,o.goods_count,o.factory,p.retail_price,p.produc_factorycolor,p.view_good_name
        from stock_order o
        left join  aa_produca p on o.goods_num=p.produc_num

            and o.danju_number = #{danjuNumber}
            and o.goods_code = #{codeOne}

		select o.goods_name,o.goods_code,o.goods_count,o.factory,p.retail_price,p.produc_factorycolor,p.view_good_name
        from stock_order o
        left join  aa_tyj p on o.goods_num=p.produc_num

            and o.danju_number = #{danjuNumber}
            and o.goods_code = #{codeOne}

select  sum(goods_count) from stock_order

   and  danju_number = #{danjuNumber}
   and  goods_type = #{goodsType}

select * FROM stock_order

    and id = #{id}
    and danju_number = #{danjuNumber}
