select c.name as ings,p.in_companyid, d.depart_name as inbm, p.in_departmentid, de.depart_name as outbm,p.out_departmentid, po.position_name as outcw ,p.out_positionid,pos.position_name as incw,p.in_positionid, p.`id`,p.`pidiao_number`, p.`zhidan_people`,p.`danju_day`,p.`outsourcing_number`,p.`beizhu`,p.`returnzt`,p.`stockorder` from stock_pidiao p left join sys_user u on p.zhidan_people =u.name
left join c_company c on p.in_companyid=c.id left join department d on p.in_departmentid=d.id left join department de on p.out_departmentid=de.id
left join c_position po on p.out_positionid=po.position_id left join c_position pos on p.in_positionid=pos.position_id where p.id = #{value}

		select
		po.position_name as outcw ,pos.position_name as incw,
		p.`id`,p.`pidiao_number`,p.`guanlian_number`,p.`out_department`,p.`out_position`,p.`in_position`,p.`in_company`,p.`in_department`,
