select po.position_id,po.`position_name`,d.`depart_name`,d.depart_number from c_position po  left join department d on po.depart_number=d.depart_number

	and d.depart_number = #{outDepartment}
	and d.depart_number = #{inDepartment}
	and po.xsstate=0 and  po.state=1

group by po.position_id order by  po.position_id

select `goods_num`,`goods_name`,`need_count`,`goods_count`, `use_count`,`goods_code`,`batch`,`zhuce_number`,`produce_day`,`useday`,`all_count`,`classtype` ,`factory`,`status` ,`username`, `goods`,`brandname`,`mfrsid` from stock_pidiao
and pidiao_number = #{pidiaoNumber}
group by id

select p.goods_num,p.goods_name,p.unit,p.use_count,p.money,position_name,p.useday,p.batch,p.zhuce_number,p.brandname from stock_pidiao p left join c_position po on p.out_positionid=po.position_id
and p.pidiao_number = #{pidiaoNumber}
group by p.id

