select
s.`id`,s.`goods_num`,s.`goods_code`,s.`goods_name`,s.`goods_count`,s.`goods_type`,s.`mfrsid`,s.`brandname`,s.`retail_price`,
s.`price_sum`, s.`cost_price`, s.`cost_sum`, s.`whole_price`, s.`whole_sum`, s.`position_name`, s.`create_time`,
s.`danju_number`, s.`order_number`, s.`yundan_number`, s.`zhidan_people`, s.`danju_day`, s.`tuihuo_number`,
s.`factory_number`, s.`beizhu`, s.`unit`,m.mfrsname,s.`batch`,s.`zhuce_number`,s.`produce_day`,s.`useday`,s.`classtype`,s.`factory`
from stock s left join c_mfrs m on s.mfrsid=m.mfrsid

    and s.id = #{id}
    and s.goods_num = #{goodsNum}
    and s.goods_code = #{goodsCode}
    and s.goods_name = #{goodsName}
    and s.goods_count = #{goodsCount}
    and s.goods_type = #{goodsType}
    and s.mfrsid = #{mfrsid}
    and s.brandname = #{brandname}
    and s.retail_price = #{retailPrice}
    and s.price_sum = #{priceSum}
    and s.cost_price = #{costPrice}
    and s.cost_sum = #{costSum}
    and s.whole_price = #{wholePrice}
    and s.whole_sum = #{wholeSum}
    and s.position_name = #{positionName}
    and s.create_time = #{createTime}
    and s.danju_number = #{danjuNumber}
    and s.order_number = #{orderNumber}
    and s.yundan_number = #{yundanNumber}
    and s.zhidan_people = #{zhidanPeople}
    and s.danju_day = #{danjuDay}
    and s.tuihuo_number = #{tuihuoNumber}
    and s.factory_number = #{factoryNumber}
    and s.beizhu = #{beizhu}
     and s.batch = #{batch} 
     and s.zhuce_number = #{zhuceNumber} 
     and s.produce_day = #{produceDay} 
     and s.useday = #{useday} 
     and s.classtype = #{classtype} 
     and s.factory = #{factory} 



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

