select
st.`id`,st.`goods_num`,st.`goods_code`,st.`goods_name`,st.`goods_count`,st.`goods_type`,st.`mfrsid`,st.`retail_price`,st.`price_sum`,st.`cost_price`,st.`cost_sum`,st.`whole_price`,
st.`whole_sum`,st.`position_name`,st.`unit`,st.`classtype`,s.`sph`,c.`cyl`,r.`refractivityvalue`,pl.`light_name`,l.`lens_name`,f.`function_name`,g.`gradual_name`,
j.`sph_up`,j.`sph_down`,j.`cyl_up`,j.`cyl_down`,j.`lightbelow_right`,j.`lightbelow_left`
from stock st left join aa_jpdz j on j.produc_num = st.goods_num
left join a_sph s on s.sph_id = j.sph_id left join a_cyl c on c.cyl_id = j.cyl_id
left join c_refractivity r on r.refractivityid = j.refractivityid
left join p_light pl on pl.light_id = j.light_id
left join p_lens l on l.lens_id = j.lens_id
left join p_gradual g on g.gradual_id = j.gradual_id
left join p_function f on f.function_id = j.function_id

    and st.id = #{id}
    and st.goods_num = #{goodsNum}
    and st.goods_code = #{goodsCode}
    and st.goods_name = #{goodsName}
    and st.goods_count = #{goodsCount}
    and st.goods_type = #{goodsType}
    and st.mfrsid = #{mfrsid}
    and st.retail_price = #{retailPrice}
    and st.price_sum = #{priceSum}
    and st.cost_price = #{costPrice}
    and st.cost_sum = #{costSum}
    and st.whole_price = #{wholePrice}
    and st.whole_sum = #{wholeSum}
    and st.position_name = #{positionName}
    and j.sph_up >= #{rightYuanYongQJ}
    and j.sph_down <= #{rightYuanYongQJ}
    and j.cyl_up >= #{rightYuanYongZJ}
    and j.cyl_down <= #{rightYuanYongZJ}
    and j.sph_up >= #{leftYuanYongQJ}
    and j.sph_down <= #{leftYuanYongQJ}
    and j.cyl_up >= #{leftYuanYongZJ}
    and j.cyl_down <= #{leftYuanYongZJ}












    and st.goods_count > 0



        order by ${sort} ${order}


        GROUP BY st.goods_name
        order by st.id desc



    limit #{offset}, #{limit}

