mapper.select:
		select `id`,`tuihuo_num`,`zhidan_people`,`danju_day`,`mfrsid`,`mfrsname`,`position_id`,`beizhu`,`goodsbrandname`,`goodsxinxiid`,`goods_num`,
		`goods_name`,`factory`,`unit`,`goods_count`,`count`,`goods_code`,`style` ,`retail_price`,`stockorder`
		from stock_tuihuo

		  		   and id = #{id} 
		  		   and tuihuo_num = #{tuihuoNum} 
		  		   and zhidan_people = #{zhidanPeople} 
		  		   and danju_day = #{danjuDay} 
		  		   and mfrsid = #{mfrsid} 
		  		   and mfrsname = #{mfrsname} 
		  		   and position_id = #{positionId} 
		  		   and beizhu = #{beizhu} 
		  		   and goodsxinxiid = #{goodsxinxiid} 
		  		   and goodsbrandname = #{goodsbrandname} 
		  		   and goods_num = #{goodsNum} 
		  		   and goods_name = #{goodsName} 
		  		   and factory = #{factory} 
		  		   and unit = #{unit} 
		  		   and goods_count = #{goodsCount} 
		  		   and count = #{count} 
		  		   and goods_code = #{goodsCode} 
		  		   and style = #{style} 
		  		   and retail_price = #{retailPrice} 
		  		   and stockorder = #{stockorder} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

