select s.goods_num,s.goods_code,s.goods_name,s.factory,s.unit,p.produc_factorycolor,m.materialname,p.size,s.retail_price,po.position_name,
s.goods_count,s.goodsxinxiid
from  stock s
left join aa_produca p on s.goodsxinxiid= p.id
left join c_material m on p.materialid=m.materialid
left join c_position po on  s.position_id=po.position_id

	 and s.mfrsid = #{mfrsid} 
	 and s.position_id = #{positionId} 
	 and s.goods_type = #{goodsType} 
	 and s.classtype = #{classtype} 
	and s.goods_num LIKE concat(concat('%', #{goodsNum}),'%')
	and s.goods_code LIKE concat(concat('%', #{goodsCode}),'%')
	and s.goods_name LIKE concat(concat('%', #{goodsName}),'%')
	 and s.brandname = #{brandname} 

group by s.goods_code

	limit #{offset}, #{limit}

