select s.goods_num, s.goods_code, s.goods_name,s.unit,s.goodsxinxiid,
j.sph_up,j.sph_down,j.cyl_up,j.cyl_down,j.lightbelow_right,j.lightbelow_left,re.refractivityvalue,l.light_name,g.gradual_name,f.function_name,
s.retail_price,s.goods_count,po.position_name
from  stock s
left join aa_jpdz j on s.goodsxinxiid= j.id
left join c_position po on  s.position_id=po.position_id
left join c_refractivity re on j.refractivityid=re.refractivityid
left join p_light l on j.light_id= l.light_id
left join p_gradual g on j.gradual_id= g.gradual_id
left join p_function f on j.function_id=f.function_id

	 and s.mfrsid = #{mfrsid} 
	 and s.position_id = #{positionId} 
	 and s.goods_type = #{goodsType} 
	 and s.classtype = #{classtype} 
	and s.goods_num LIKE concat(concat('%', #{goodsNum}),'%')
	and s.goods_code LIKE concat(concat('%', #{goodsCode}),'%')
	and s.goods_name LIKE concat(concat('%', #{goodsName}),'%')
	 and s.brandname = #{brandname} 

group by s.goods_code

	limit #{offset}, #{limit}

