select s.goods_num,s.goods_code,s.goods_name,s.unit,sp.sph,cy.cyl,l.lightbelow,r.refractivityvalue,li.light_name,g.gradual_name, f.function_name,s.retail_price,po.position_name,s.goods_count,s.goodsxinxiid from stock s left join aa_jpcp j on s.goodsxinxiid= j.id left join a_sph sp on j.sph_id=sp.sph_id left join a_cyl cy on j.cyl_id=cy.cyl_id left join a_lightbelow l on j.lightbelow_id=l.lightbelow_id left join c_refractivity r on j.refractivityid=r.refractivityid left join p_light li on j.light_id=li.light_id left join p_gradual g on j.gradual_id = g.gradual_id left join p_function f on j.function_id=f.function_id left join c_position po on s.position_id=po.position_id
and s.mfrsid = #{mfrsid} and s.position_id = #{positionId} and s.goods_type = #{goodsType} and s.classtype = #{classtype} and s.goods_num LIKE concat(concat('%', #{goodsNum}),'%') and s.goods_code LIKE concat(concat('%', #{goodsCode}),'%') and s.goods_name LIKE concat(concat('%', #{goodsName}),'%') and s.brandname = #{brandname}
group by s.goods_code
limit #{offset}, #{limit}

