select t.`id`,t.`tuihuo_num`,t.`zhidan_people`,t.`danju_day`,t.`mfrsid`,m.`mfrsname`,t.`position_id`,p.position_name,t.`beizhu`,t.`style`
from stock_tuihuo t
left join c_mfrs m on t.mfrsid=m.mfrsnum
left join c_position p on t.position_id=t.position_id

	 and id = #{id} 
	 and tuihuo_num = #{tuihuoNum} 
	 and zhidan_people = #{zhidanPeople} 
	 and danju_day = #{danjuDay} 
	 and mfrsid = #{mfrsid} 
	 and mfrsname = #{mfrsname} 
	 and position_id = #{positionId} 
	 and beizhu = #{beizhu} 
	 and goodsxinxiid = #{goodsxinxiid} 
	 and goods_num = #{goodsNum} 
	 and goods_name = #{goodsName} 
	 and factory = #{factory} 
	 and unit = #{unit} 
	 and goods_count = #{goodsCount} 
	 and count = #{count} 
	 and goods_code = #{goodsCode} 
	 and style = #{style} 

group by tuihuo_num  order by id desc

	limit #{offset}, #{limit}

