select
s.`id`,s.`danju_number`,s.`danju_day`,s.`eye_style`,s.`zhidan_people`,s.`mfrsid`,s.`mfrsname`,s.`goodsmfrsid`,s.`goodsbrandnum`,s.`goodsbrandname`,
s.`position_id`,s.`position_name`,s.`changjia_number`,s. `shouhuo_people`,s.`shouhuo_phone`,s.`shouhuo_address`,s.`beizhu`,
s.`sale_number`,s.`mirror_time`,s.`goods_name`,s.`num`,s.`code`,
s.`style`,s.`rl`,s.`count`,s.`sph`,s.`cyl`,s.`axial`,s.`lightbelow`,s.`lengjing`,s.`diameter`,s.`yaoqiu`,s.`jd`,
s.`curvature`,s.`gkname`,s.`hyknum`,s.`phone`,
s.`status`,s.`username`,s.`sh_time`,s.`shstatus` ,s.`psname`,s.`salenumbery`,s.`pstime`,s.`stockorder`
from stock_weiwai s
left join c_position po on s.position_id=po.position_id
left join department d on po.depart_number= d.depart_number

    and s.id = #{id}
    and s.danju_number  LIKE concat(concat('%', #{danjuNumber}),'%')
    and s.danju_day = #{danjuDay}
    and s.eye_style = #{eyeStyle}
    and s.zhidan_people = #{zhidanPeople}
    and s.mfrsid = #{mfrsid}
    and s.mfrsname  LIKE concat(concat('%', #{mfrsname}),'%')
    and s.goodsmfrsid  LIKE concat(concat('%', #{goodsmfrsid}),'%')
    and s.goodsbrandnum  LIKE concat(concat('%', #{goodsbrandnum}),'%')
    and s.goodsbrandname  LIKE concat(concat('%', #{goodsbrandname}),'%')
    and s.position_id = #{positionId}
    and s.position_name = #{positionName}
    and s.changjia_number = #{changjiaNumber}
    and s.shouhuo_people = #{shouhuoPeople}
    and s.shouhuo_phone = #{shouhuoPhone}
    and s.shouhuo_address = #{shouhuoAddress}
    and s.beizhu = #{beizhu}

    and s.sale_number = #{saleNumber}
    and s.mirror_time = #{mirrorTime}
    and s.goods_name = #{goodsName}
    and s.num = #{num}
    and s.code = #{code}
    and s.style = #{style}
    and s.rl = #{rl}
    and s.count = #{count}
    and s.sph = #{sph}
    and s.cyl = #{cyl}
    and s.axial = #{axial}
    and s.lightbelow = #{lightbelow}
    and s.lengjing = #{lengjing}
    and s.diameter = #{diameter}
    and s.yaoqiu = #{yaoqiu}
    and s.jd = #{jd}
    and s.curvature = #{curvature}


    and s.gkname  LIKE concat(concat('%', #{gkname}),'%')
    and s.hyknum  LIKE concat(concat('%', #{hyknum}),'%')
    and s.phone  LIKE concat(concat('%', #{phone}),'%')

    and s.status = #{status}
    and s.username = #{username}
    and s.sh_time = #{shTime}
    and s.shstatus = #{shstatus}
    and s.psname = #{psname}



    and s.salenumbery  LIKE concat(concat('%', #{salenumbery}),'%')
    and s.pstime = #{pstime}
    and s.stockorder = #{stockorder}
    and po.depart_number = #{departNumber}
    and FIND_IN_SET(d.company_id,#{companyid})



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

