select count(*)
from settlement s
left join member m on s.member_number=m.card_number
left join store_sales sa on s.sale_number=sa.sale_number

	 and s.id = #{id} 
	 and s.sale_number = #{saleNumber} 
	 and s.shouhuo_number = #{shouhuoNumber} 
	 and s.position_id = #{positionId} 
	 and s.mfrsid = #{mfrsid} 
	 and s.name = #{name} 
	 and s.phone = #{phone} 
	 and s.dz_stype = #{dzStype} 
	 and s.mirror_time = #{mirrorTime} 
	 and s.eye_right = #{eyeRight} 
	 and s.eye_left = #{eyeLeft} 
	 and s.depart_number = #{departNumber} 
	 and s.sph = #{sph} 
	 and s.cyl = #{cyl} 
	 and s.goods_name = #{goodsName} 
	 and s.rl = #{rl} 
	 and s.goods_num = #{goodsNum} 
	 and s.factory = #{factory} 
	 and s.count = #{count} 
	 and s.goods_code = #{goodsCode}

