		  		   and id = #{id} 
		  		   and sale_number = #{saleNumber} 
		  		   and shouhuo_number = #{shouhuoNumber} 
		  		   and position_id = #{positionId} 
		  		   and mfrsid = #{mfrsid} 
		  		   and name = #{name} 
		  		   and phone = #{phone} 
		  		   and dz_stype = #{dzStype} 
		  		   and mirror_time = #{mirrorTime} 
		  		   and eye_right = #{eyeRight} 
		  		   and eye_left = #{eyeLeft} 
		  		   and depart_number = #{departNumber} 
		  		   and sph = #{sph} 
		  		   and cyl = #{cyl} 
		  		   and goods_name = #{goodsName} 
