select count(*) from stock_weiwai

  		   and id = #{id} 
  		   and sale_number = #{saleNumber} 
  		   and shouhuo_number = #{shouhuoNumber} 
  		   and position_id = #{positionId} 
  		   and mfrsid = #{mfrsid} 
  		   and name = #{name} 
  		   and phone = #{phone} 
  		   and dz_stype = #{dzStype} 
  		   and mirror_time = #{mirrorTime} 
  		   and eye_right = #{eyeRight} 
  		   and eye_left = #{eyeLeft} 
  		   and depart_number = #{departNumber} 
  		   and sph = #{sph} 
  		   and cyl = #{cyl} 
  		   and goods_name = #{goodsName} 
  		   and rl = #{rl} 
  		   and goods_num = #{goodsNum} 
  		   and factory = #{factory} 
  		   and count = #{count} 
  		   and goods_code = #{goodsCode}

select count(*) from settlement s
left join member m on s.member_number=m.card_number
left join store_sales sa on s.sale_number=sa.sale_number

	 and s.id = #{id} 
	 and s.sale_number = #{saleNumber} 
	 and s.shouhuo_number = #{shouhuoNumber} 
	 and s.position_id = #{positionId} 
	 and s.mfrsid = #{mfrsid} 
	 and s.name = #{name} 
	 and s.phone = #{phone} 
	 and s.dz_stype = #{dzStype} 
	 and s.mirror_time = #{mirrorTime} 
	 and s.eye_right = #{eyeRight} 
	 and s.eye_left = #{eyeLeft} 
	 and s.depart_number = #{departNumber} 
	 and s.sph = #{sph} 
	 and s.cyl = #{cyl} 
	 and s.goods_name = #{goodsName} 
	 and s.rl = #{rl} 
	 and s.goods_num = #{goodsNum} 
	 and s.factory = #{factory} 
	 and s.count = #{count} 
	 and s.goods_code = #{goodsCode}
